import type { Express } from 'express';
import { isAdmin } from '../../lib/auth';
import { listModels, upsertModel, deleteModel, getModelByName } from '../../lib/models';
import { importOpenAIModels, seedAnthropicModels, seedGeminiModels, listOpenAIModels } from '../../lib/modelImporters';
import { getSettings, updateSettings } from '../../lib/settings';

export default function mount(app: Express){
  app.get('/models', async (req, res) => {
    if (!isAdmin(req)) return res.status(401).json({ error: 'auth required', login: '/ui/login' });
    const rows = await listModels();
    res.json({ models: rows });
  });
  app.post('/models', async (req, res) => {
    if (!isAdmin(req)) return res.status(401).json({ error: 'auth required', login: '/ui/login' });
    try {
      const m = await upsertModel(req.body || {});
      res.status(201).json(m);
    } catch (e:any) {
      res.status(400).json({ error: e.message });
    }
  });
  app.post('/models/preview/openai', async (req, res) => {
    if (!isAdmin(req)) return res.status(401).json({ error: 'auth required', login: '/ui/login' });
    try {
      const filterRaw = (req.body && (req.body.filter || req.body.includes)) || '';
      const excludeRaw = (req.body && (req.body.exclude || req.body.excludes)) || '';
      const filter = Array.isArray(filterRaw) ? filterRaw : String(filterRaw).split(',').map((s:string)=>s.trim()).filter(Boolean);
      const exclude = Array.isArray(excludeRaw) ? excludeRaw : String(excludeRaw).split(',').map((s:string)=>s.trim()).filter(Boolean);
      const recommendedOnly = filter.length === 0 ? !!(req.body && (req.body.recommendedOnly ?? true)) : false;
      const candidates = await listOpenAIModels({ filter, exclude, recommendedOnly });
      res.json({ candidates, count: candidates.length });
    } catch (e:any) {
      res.status(400).json({ error: e.message });
    }
  });
  app.post('/models/import/:vendor', async (req, res) => {
    if (!isAdmin(req)) return res.status(401).json({ error: 'auth required', login: '/ui/login' });
    const v = (req.params.vendor || '').toLowerCase();
    try {
      const namesRaw = req.body && (req.body.names || req.body.list);
      const names = Array.isArray(namesRaw)
        ? namesRaw
        : (typeof namesRaw === 'string' ? namesRaw.split(',').map((s:string)=>s.trim()).filter(Boolean) : []);
      if (names.length && (v === 'anthropic' || v === 'gemini')) {
        let count = 0;
        for (const n of names) {
          await upsertModel({ provider: v, name: n, kind: v, display_name: n });
          count++;
        }
        return res.json({ imported: count, via: 'custom-list' });
      }
      if (v === 'openai') {
        // If explicit names are provided, upsert just those.
        if (names.length) {
          let count = 0;
          for (const n of names) {
            await upsertModel({ provider: 'openai', name: n, kind: 'openai', display_name: n });
            count++;
          }
          return res.json({ imported: count, via: 'explicit-list' });
        }
        const filterRaw = (req.body && (req.body.filter || req.body.includes)) || '';
        const excludeRaw = (req.body && (req.body.exclude || req.body.excludes)) || '';
        const filter = Array.isArray(filterRaw) ? filterRaw : String(filterRaw).split(',').map((s:string)=>s.trim()).filter(Boolean);
        const exclude = Array.isArray(excludeRaw) ? excludeRaw : String(excludeRaw).split(',').map((s:string)=>s.trim()).filter(Boolean);
        // If user provided filters, don't restrict to recommended only.
        const recommendedOnly = filter.length === 0 ? !!(req.body && (req.body.recommendedOnly ?? true)) : false;
        const r = await importOpenAIModels({ filter, exclude, recommendedOnly });
        return res.json(r);
      }
      if (v === 'anthropic') return res.json(await seedAnthropicModels());
      if (v === 'gemini') return res.json(await seedGeminiModels());
      return res.status(400).json({ error: 'unknown vendor' });
    } catch (e:any) {
      return res.status(400).json({ error: e.message });
    }
  });

  app.post('/models/promote/gemini', async (req, res) => {
    if (!isAdmin(req)) return res.status(401).json({ error: 'auth required', login: '/ui/login' });
    try {
      const from = 'gemini-1.5-pro';
      const to = 'gemini-2.5-pro';
      const fromModel = await getModelByName(from);
      await upsertModel({
        provider: 'gemini',
        name: to,
        kind: 'gemini',
        display_name: 'Gemini 2.5 Pro',
        input_per_1m: fromModel?.input_per_1m,
        output_per_1m: fromModel?.output_per_1m,
        active: true
      } as any);
      const settings = await getSettings();
      const mo = settings.llm.modelOrder || {} as any;
      function repl(arr?: string[]){
        return Array.isArray(arr) ? arr.map(x => x === from ? to : x) : [];
      }
      const next = {
        llm: {
          modelOrder: {
            docs: repl(mo.docs),
            reasoning: repl(mo.reasoning),
            codegen: repl(mo.codegen)
          }
        }
      } as any;
      await updateSettings(next);
      res.json({ ok: true, updatedModel: to });
    } catch (e:any) {
      res.status(400).json({ error: e.message });
    }
  });
  app.delete('/models/:id', async (req, res) => {
    if (!isAdmin(req)) return res.status(401).json({ error: 'auth required', login: '/ui/login' });
    await deleteModel(req.params.id);
    res.json({ ok: true });
  });
}

import type { Express } from 'express';
import { getSettings, updateSettings } from '../../lib/settings';
import { query } from '../../lib/db';
import { isAdmin } from '../../lib/auth';

export default function mount(app: Express){
  app.get('/settings', async (req, res) => {
    if (!isAdmin(req)) return res.status(401).json({ error: 'auth required', login: '/ui/login' });
    const settings = await getSettings();
    const rules = await query<any>(`select table_name, allowed_ops, constraints from nofx.db_write_rule where tenant_id='local' order by table_name`)
      .catch(()=>({ rows: [] as any[] }));
    res.json({ settings, db_write_rules: rules.rows });
  });

  app.post('/settings', async (req, res) => {
    if (!isAdmin(req)) return res.status(401).json({ error: 'auth required', login: '/ui/login' });
    const body = req.body || {};
    const next = await updateSettings(body.settings || {});
    if (Array.isArray(body.db_write_rules)) {
      // replace all rules for tenant 'local' with provided set (simple local UX)
      await query(`delete from nofx.db_write_rule where tenant_id='local'`).catch(()=>{});
      for (const r of body.db_write_rules) {
        if (!r || !r.table_name || !Array.isArray(r.allowed_ops)) continue;
        await query(
          `insert into nofx.db_write_rule (tenant_id, table_name, allowed_ops, constraints)
           values ('local', $1, $2::text[], $3::jsonb)
           on conflict (tenant_id, table_name) do update set allowed_ops=excluded.allowed_ops, constraints=excluded.constraints`,
          [r.table_name, r.allowed_ops, r.constraints || {}]
        ).catch(()=>{});
      }
    }
    res.json({ ok: true, settings: next });
  });
}

import type { Express } from 'express';
import { query } from '../../lib/db';
import { supabase, ARTIFACT_BUCKET } from '../../lib/supabase';
import { isAdmin } from '../../lib/auth';

export default function mount(app: Express){
  app.get('/ui/runs', async (_req, res) => {
    const rows = await query<any>(`select id,status,created_at from nofx.run order by created_at desc limit 100`);
    res.render('runs', { runs: rows.rows });
  });
  // Place the 'new' route BEFORE the ':id' route to avoid param capture
  app.get('/ui/runs/new', async (_req, res) => {
    res.render('new_run');
  });
  app.get('/ui/runs/:id', async (req, res) => {
    const runId = req.params.id;
    const run = await query<any>(`select * from nofx.run where id = $1`, [runId]);
    const artifacts = await query<any>(
      `select a.*, coalesce(a.uri, a.path) as uri, s.name as step_name from nofx.artifact a join nofx.step s on s.id = a.step_id where s.run_id = $1`, [runId]
    );
    const gates = await query<any>(`select * from nofx.gate where run_id=$1 order by created_at asc`, [runId]);
    res.render('run', { run: run.rows[0], artifacts: artifacts.rows, gates: gates.rows });
  });
  app.get('/ui/settings', async (req, res) => {
    if (!isAdmin(req)) return res.redirect('/ui/login');
    res.render('settings');
  });
  app.get('/ui/models', async (req, res) => {
    if (!isAdmin(req)) return res.redirect('/ui/login');
    res.render('models');
  });
  app.get('/ui/artifacts/signed', async (req, res) => {
    const path = String(req.query.path || '');
    const { data, error } = await supabase.storage.from(ARTIFACT_BUCKET).createSignedUrl(path, 3600);
    if (error || !data) return res.status(404).send('not found');
    res.redirect(data.signedUrl);
  });
}

