import { query } from './db';

export type ApprovalsSettings = {
  dbWrites: 'none'|'dangerous'|'all';
  allowWaive: boolean;
};
export type GatesSettings = {
  typecheck: boolean;
  lint: boolean;
  unit: boolean;
  coverageThreshold: number; // 0..1
};
export type Settings = {
  approvals: ApprovalsSettings;
  gates: GatesSettings;
  llm: LlmSettings;
};

export type LlmSettings = {
  order: {
    codegen: Array<'openai'|'anthropic'|'gemini'>;
    reasoning: Array<'openai'|'anthropic'|'gemini'>;
    docs: Array<'openai'|'anthropic'|'gemini'>;
  };
  modelOrder?: { docs?: string[]; reasoning?: string[]; codegen?: string[] };
  providers?: Record<string, { kind: 'openai-compatible'|'http'; baseUrl?: string }>;
  pricing?: Record<string, { inputPer1M?: number; outputPer1M?: number }>;
};

const DEFAULTS: Settings = {
  approvals: { dbWrites: 'dangerous', allowWaive: true },
  gates: { typecheck: true, lint: true, unit: true, coverageThreshold: 0.9 },
  llm: {
    order: {
      codegen: ['openai','anthropic','gemini'],
      reasoning: ['anthropic','openai','gemini'],
      docs: ['gemini','anthropic','openai']
    },
    modelOrder: { docs: [], reasoning: [], codegen: [] },
    providers: {},
    pricing: {}
  }
};

async function ensureSettingsSchema() {
  try {
    await query(`alter table nofx.settings add column if not exists llm jsonb not null default '{}'::jsonb`);
  } catch {}
}

export async function getSettings(): Promise<Settings> {
  try {
    await ensureSettingsSchema();
    const r = await query<{ approvals: any; gates: any; llm: any }>(`select approvals, gates, llm from nofx.settings where id='default' limit 1`);
    if (!r.rows[0]) return DEFAULTS;
    const approvals = { ...DEFAULTS.approvals, ...(r.rows[0].approvals || {}) };
    const gates = { ...DEFAULTS.gates, ...(r.rows[0].gates || {}) };
    const llm: LlmSettings = {
      order: {
        codegen: (r.rows[0].llm?.order?.codegen || DEFAULTS.llm.order.codegen),
        reasoning: (r.rows[0].llm?.order?.reasoning || DEFAULTS.llm.order.reasoning),
        docs: (r.rows[0].llm?.order?.docs || DEFAULTS.llm.order.docs)
      },
      modelOrder: {
        docs: r.rows[0].llm?.modelOrder?.docs || [],
        reasoning: r.rows[0].llm?.modelOrder?.reasoning || [],
        codegen: r.rows[0].llm?.modelOrder?.codegen || []
      },
      providers: r.rows[0].llm?.providers || {},
      pricing: r.rows[0].llm?.pricing || {}
    };
    return { approvals, gates, llm };
  } catch {
    return DEFAULTS; // schema may not be migrated yet; fail-safe defaults
  }
}

export async function updateSettings(patch: Partial<Settings>): Promise<Settings> {
  const current = await getSettings();
  const next: Settings = {
    approvals: { ...current.approvals, ...(patch.approvals || {}) },
    gates: { ...current.gates, ...(patch.gates || {}) },
    llm: {
      order: {
        codegen: (patch as any).llm?.order?.codegen || current.llm.order.codegen,
        reasoning: (patch as any).llm?.order?.reasoning || current.llm.order.reasoning,
        docs: (patch as any).llm?.order?.docs || current.llm.order.docs,
      },
      modelOrder: {
        docs: (patch as any).llm?.modelOrder?.docs || current.llm.modelOrder?.docs || [],
        reasoning: (patch as any).llm?.modelOrder?.reasoning || current.llm.modelOrder?.reasoning || [],
        codegen: (patch as any).llm?.modelOrder?.codegen || current.llm.modelOrder?.codegen || []
      },
      providers: (patch as any).llm?.providers || current.llm.providers || {},
      pricing: normalizePricing((patch as any).llm?.pricing || current.llm.pricing || {})
    }
  };
  try {
    await ensureSettingsSchema();
    await query(
      `insert into nofx.settings (id, approvals, gates, llm)
       values ('default', $1, $2, $3)
       on conflict(id) do update set approvals=excluded.approvals, gates=excluded.gates, llm=excluded.llm, updated_at=now()`,
      [next.approvals, next.gates, next.llm]
    );
  } catch (e) {
    // fallback for older schema without llm
    await query(
      `insert into nofx.settings (id, approvals, gates)
       values ('default', $1, $2)
       on conflict(id) do update set approvals=excluded.approvals, gates=excluded.gates, updated_at=now()`,
      [next.approvals, next.gates]
    );
  }
  return next;
}

function normalizePricing(input: any): LlmSettings['pricing'] {
  const out: LlmSettings['pricing'] = {};
  const keys = input ? Object.keys(input) : [];
  for (const provider of keys) {
    const p = input?.[provider] || {};
    let inPer1M = p.inputPer1M;
    let outPer1M = p.outputPer1M;
    // Backward-compat: convert per 1K to per 1M if present
    if ((inPer1M == null) && p.inputPer1K != null) inPer1M = Number(p.inputPer1K) * 1000;
    if ((outPer1M == null) && p.outputPer1K != null) outPer1M = Number(p.outputPer1K) * 1000;
    if (inPer1M != null || outPer1M != null) {
      (out as any)[provider] = { inputPer1M: inPer1M, outputPer1M: outPer1M };
    }
  }
  return out;
}

import Anthropic from '@anthropic-ai/sdk';
export async function claudeChat(prompt: string, model=process.env.ANTHROPIC_MODEL || 'claude-3-5-sonnet-latest'){
  const client = new Anthropic({ apiKey: process.env.ANTHROPIC_API_KEY! });
  const started = Date.now();
  const rsp = await client.messages.create({ model, max_tokens:800, messages:[{role:'user', content: prompt}]});
  const txt = (rsp.content[0] as any)?.text || '';
  const usage = (rsp as any).usage ? {
    inputTokens: (rsp as any).usage.input_tokens,
    outputTokens: (rsp as any).usage.output_tokens,
    totalTokens: ((rsp as any).usage.input_tokens || 0) + ((rsp as any).usage.output_tokens || 0),
    latencyMs: Date.now() - started
  } : undefined;
  return { text: txt.trim(), provider: 'anthropic', model, usage };
}

import { GoogleGenerativeAI } from '@google/generative-ai';
export async function geminiChat(prompt: string, model=process.env.GEMINI_MODEL || 'gemini-2.5-pro'){
  const genai = new GoogleGenerativeAI(process.env.GEMINI_API_KEY!);
  const modelRef = genai.getGenerativeModel({ model });
  const started = Date.now();
  const rsp = await modelRef.generateContent(prompt);
  const text = rsp.response.text().trim();
  const um: any = (rsp.response as any).usageMetadata || {};
  const usage = (um.promptTokenCount || um.candidatesTokenCount) ? {
    inputTokens: um.promptTokenCount,
    outputTokens: um.candidatesTokenCount,
    totalTokens: (um.totalTokenCount) || (um.promptTokenCount || 0) + (um.candidatesTokenCount || 0),
    latencyMs: Date.now() - started
  } : undefined;
  return { text, provider: 'gemini', model, usage };
}

import { openaiChat } from './providers/openai';
import { claudeChat } from './providers/anthropic';
import { geminiChat } from './providers/gemini';
import { getSettings } from '../lib/settings';
import { httpChat } from './providers/http';
import { getModelByName } from '../lib/models';

export type TaskKind = 'codegen'|'reasoning'|'docs';

export async function routeLLM(kind: TaskKind, prompt: string){
  const { llm } = await getSettings();
  const modelOrder = llm?.modelOrder?.[kind as 'docs'|'reasoning'|'codegen'] || [];
  let lastErr: any;
  if (modelOrder.length) {
    for (const name of modelOrder) {
      try {
        const m = await getModelByName(name);
        if (!m || m.active === false) continue;
        const out: any = await callModelWithRetry(m, prompt);
        if (typeof out === 'string') return out;
        return { ...out, provider: m.provider, model: m.name };
      } catch (e:any) { lastErr = e; }
    }
    throw lastErr || new Error('no model succeeded');
  }
  const order = await pickOrder(kind);
  for (const p of order) {
    try {
      const model = (p==='openai' ? process.env.OPENAI_MODEL : p==='anthropic' ? process.env.ANTHROPIC_MODEL : p==='gemini' ? process.env.GEMINI_MODEL : undefined);
      const out = await callWithRetry(p, prompt, model as any);
      return out;
    } catch (e:any) { lastErr = e; }
  }
  throw lastErr || new Error('no provider succeeded');
}
async function pickOrder(kind: TaskKind): Promise<Array<'openai'|'anthropic'|'gemini'>> {
  const envPref = (process.env.LLM_ORDER || '').split(',').map(x=>x.trim().toLowerCase()).filter(Boolean) as any[];
  if (envPref.length) return envPref as any;
  try {
    const { llm } = await getSettings();
    const order = llm?.order?.[kind as 'docs'|'reasoning'|'codegen'];
    if (Array.isArray(order) && order.length) return order as any;
  } catch {}
  if (kind === 'codegen') return ['openai','anthropic','gemini'];
  if (kind === 'reasoning') return ['anthropic','openai','gemini'];
  return ['gemini','anthropic','openai'];
}
function call(p: string, prompt: string, model?: string, custom?: any){
  if (p==='openai') return openaiChat(prompt, model);
  if (p==='anthropic') return claudeChat(prompt, model);
  if (p==='gemini') return geminiChat(prompt, model);
  if (custom && custom.kind === 'openai-compatible') {
    const baseURL = custom.baseUrl || process.env[`LLM_${p.toUpperCase()}_BASE_URL`];
    const apiKeyEnv = process.env[`LLM_${p.toUpperCase()}_API_KEY`] ? `LLM_${p.toUpperCase()}_API_KEY` : undefined;
    return openaiChat(prompt, model, { baseURL, apiKeyEnv });
  }
  if (custom && custom.kind === 'http') {
    const endpoint = custom.baseUrl || process.env[`LLM_${p.toUpperCase()}_BASE_URL`];
    if (!endpoint) throw new Error('http provider missing baseUrl');
    const apiKeyEnv = process.env[`LLM_${p.toUpperCase()}_API_KEY`] ? `LLM_${p.toUpperCase()}_API_KEY` : undefined;
    return httpChat(prompt, endpoint, apiKeyEnv, model);
  }
  throw new Error('unknown provider ' + p);
}

async function callWithRetry(p: string, prompt: string, model: string|undefined, retries=2, timeoutMs=15000){
  let lastErr: any;
  for (let attempt=0; attempt<=retries; attempt++){
    try {
      const { llm } = await getSettings();
      const custom = llm?.providers ? (llm.providers as any)[p] : undefined;
      const res = await withTimeout(call(p, prompt, model, custom), timeoutMs);
      return res;
    } catch (e:any) {
      lastErr = e;
      await delay((attempt+1)*250);
    }
  }
  throw lastErr;
}

async function callModelWithRetry(m: { kind: string; base_url?: string; provider: string; name: string }, prompt: string, retries=2, timeoutMs=15000){
  let lastErr: any;
  for (let attempt=0; attempt<=retries; attempt++){
    try {
      const res = await withTimeout(callModel(m, prompt), timeoutMs);
      return res;
    } catch (e:any) {
      lastErr = e;
      await delay((attempt+1)*250);
    }
  }
  throw lastErr;
}

function callModel(m: { kind: string; base_url?: string; provider: string; name: string }, prompt: string){
  const kind = (m.kind || '').toLowerCase();
  if (kind === 'openai') return openaiChat(prompt, m.name);
  if (kind === 'anthropic') return claudeChat(prompt, m.name);
  if (kind === 'gemini') return geminiChat(prompt, m.name);
  if (kind === 'openai-compatible') {
    const apiKeyEnv = `LLM_${m.provider.toUpperCase()}_API_KEY`;
    return openaiChat(prompt, m.name, { baseURL: m.base_url, apiKeyEnv });
  }
  if (kind === 'http') {
    const apiKeyEnv = `LLM_${m.provider.toUpperCase()}_API_KEY`;
    if (!m.base_url) throw new Error('http model missing base_url');
    return httpChat(prompt, m.base_url, apiKeyEnv, m.name);
  }
  throw new Error('unknown model kind ' + kind);
}

function withTimeout<T>(p: Promise<T>, ms: number): Promise<T> {
  return new Promise((resolve, reject) => {
    const t = setTimeout(() => reject(new Error('llm timeout')), ms);
    p.then(v => { clearTimeout(t); resolve(v); }, err => { clearTimeout(t); reject(err); });
  });
}

function delay(ms:number){ return new Promise(r=>setTimeout(r, ms)); }

import { query } from "../lib/db";
export type DbOp = 'insert'|'update'|'delete';
export async function isAllowed(table: string, op: DbOp){
  const r = await query<any>(`select allowed_ops, constraints from nofx.db_write_rule where tenant_id='local' and table_name=$1`, [table]);
  if (!r.rows[0]) return { ok:false, reason:'no rule' };
  const ops: string[] = r.rows[0].allowed_ops || [];
  if (!ops.includes(op)) return { ok:false, reason:'op not allowed' };
  return { ok:true, constraints: r.rows[0].constraints || {} };
}

