import { StepHandler } from "./types";
import { query } from "../../lib/db";
import { recordEvent } from "../../lib/events";
import { isAllowed, DbOp } from "../../policy/dbWritePolicy";
import { getSettings } from "../../lib/settings";
import { enqueue, STEP_READY_TOPIC } from "../../lib/queue";

type Inputs = {
  table: string;
  op: DbOp;
  values?: Record<string, any>;
  where?: string;          // SQL where clause with $1,$2 params
  whereParams?: any[];     // parameter values for where
};

function isSafeIdent(v: string){
  return /^[a-zA-Z0-9_.]+$/.test(v);
}
function isSafeColumn(v: string){
  return /^[a-zA-Z0-9_]+$/.test(v);
}

const handler: StepHandler = {
  match: (tool) => tool === 'db_write',
  async run({ runId, step }) {
    const stepId = step.id;
    await query(`update nofx.step set status='running', started_at=now() where id=$1`, [stepId])
      .catch(async ()=>{ await query(`update nofx.step set status='running' where id=$1`, [stepId]); });
    await recordEvent(runId, 'step.started', { name: step.name, tool: step.tool }, stepId);

    const inputs: Inputs = step.inputs || {};
    if (!inputs.table || !inputs.op) throw new Error('db_write requires table and op');
    if (!isSafeIdent(inputs.table)) throw new Error('unsafe table name');

    // Check approval policy for db writes
    const { approvals } = await getSettings();
    let needsApproval = false;
    if (approvals.dbWrites === 'all') needsApproval = true;
    if (approvals.dbWrites === 'dangerous' && (inputs.op === 'update' || inputs.op === 'delete')) needsApproval = true;

    if (needsApproval) {
      const g = await query<any>(`select * from nofx.gate where run_id=$1 and step_id=$2 and gate_type='manual:db' order by created_at desc limit 1`, [runId, stepId]);
      if (!g.rows[0]) {
        await query(`insert into nofx.gate (run_id, step_id, gate_type, status) values ($1,$2,'manual:db','pending')`, [runId, stepId]);
        await recordEvent(runId, 'gate.created', { stepId, tool: 'manual:db' }, stepId);
        await enqueue(STEP_READY_TOPIC, { runId, stepId }, { delay: 5000 });
        await recordEvent(runId, 'gate.waiting', { stepId, delayMs: 5000 }, stepId);
        return;
      }
      const gate = g.rows[0];
      if (gate.status === 'pending') {
        await enqueue(STEP_READY_TOPIC, { runId, stepId }, { delay: 5000 });
        await recordEvent(runId, 'gate.waiting', { stepId, delayMs: 5000 }, stepId);
        return;
      }
      if (gate.status === 'failed') {
        await query(`update nofx.step set status='failed', ended_at=now(), error=$2 where id=$1`, [stepId, 'db write not approved'])
          .catch(async ()=>{ await query(`update nofx.step set status='failed', completed_at=now(), error=$2 where id=$1`, [stepId, 'db write not approved']); });
        await recordEvent(runId, 'step.failed', { stepId, tool: step.tool, manual: true, gateId: gate.id }, stepId);
        throw new Error('db write not approved');
      }
      // passed or waived -> continue
    }

    const allowed = await isAllowed(inputs.table, inputs.op);
    if (!allowed.ok) {
      await query(`update nofx.step set status='failed', ended_at=now(), error=$2 where id=$1`, [stepId, `policy: ${allowed.reason}`])
        .catch(async ()=>{ await query(`update nofx.step set status='failed', completed_at=now(), error=$2 where id=$1`, [stepId, `policy: ${allowed.reason}`]); });
      await recordEvent(runId, 'db.write.denied', { table: inputs.table, op: inputs.op, reason: allowed.reason }, stepId);
      throw new Error('db_write not allowed');
    }

    let sql = '';
    const params: any[] = [];
    let result: any = {};
    if (inputs.op === 'insert') {
      const vals = inputs.values || {};
      const cols = Object.keys(vals);
      if (cols.length === 0) throw new Error('insert requires values');
      for (const c of cols) if (!isSafeColumn(c)) throw new Error('unsafe column');
      const placeholders = cols.map((_, i) => `$${i + 1}`);
      params.push(...cols.map(c => vals[c]));
      sql = `insert into ${inputs.table} (${cols.join(',')}) values (${placeholders.join(',')}) returning *`;
    } else if (inputs.op === 'update') {
      const vals = inputs.values || {};
      const cols = Object.keys(vals);
      if (cols.length === 0) throw new Error('update requires values');
      if (!inputs.where) throw new Error('update requires where');
      for (const c of cols) if (!isSafeColumn(c)) throw new Error('unsafe column');
      const sets = cols.map((c, i) => `${c} = $${i + 1}`);
      params.push(...cols.map(c => vals[c]));
      const remappedWhere = remapPlaceholders(inputs.where!, params.length);
      params.push(...(inputs.whereParams || []));
      sql = `update ${inputs.table} set ${sets.join(', ')} where ${remappedWhere} returning *`;
    } else if (inputs.op === 'delete') {
      if (!inputs.where) throw new Error('delete requires where');
      const remappedWhere = remapPlaceholders(inputs.where!, params.length);
      params.push(...(inputs.whereParams || []));
      sql = `delete from ${inputs.table} where ${remappedWhere} returning *`;
    } else {
      throw new Error('unknown op');
    }

    const q = await query<any>(sql, params);
    result = { rowCount: (q as any).rows?.length ?? 0, rows: (q as any).rows ?? [] };

    await query(`update nofx.step set status='succeeded', outputs=$2, ended_at=now() where id=$1`, [
      stepId,
      JSON.stringify({ table: inputs.table, op: inputs.op, result })
    ]).catch(async ()=>{
      await query(`update nofx.step set status='succeeded', outputs=$2, completed_at=now() where id=$1`, [
        stepId,
        JSON.stringify({ table: inputs.table, op: inputs.op, result })
      ]);
    });
    await recordEvent(runId, 'db.write.succeeded', { table: inputs.table, op: inputs.op, rowCount: result.rowCount }, stepId);
  }
};

export default handler;

// Remap $1..$N in a WHERE clause to start at current parameter offset.
function remapPlaceholders(where: string, currentParams: number): string {
  return where.replace(/\$(\d+)/g, (_m, g1) => {
    const n = parseInt(g1, 10);
    if (!Number.isFinite(n) || n <= 0) return _m;
    return `$${currentParams + n}`;
  });
}

import { StepHandler } from "./types";
import { query } from "../../lib/db";
import { recordEvent } from "../../lib/events";
import { supabase, ARTIFACT_BUCKET } from "../../lib/supabase";
import { log } from "../../lib/logger";
import { getSettings } from "../../lib/settings";
import { spawnSync } from "node:child_process";
import fs from "node:fs";
import path from "node:path";

function contentTypeFor(name: string) {
  const ext = path.extname(name).toLowerCase();
  if (ext === ".json") return "application/json";
  if (ext === ".txt" || ext === ".log") return "text/plain";
  return "application/octet-stream";
}

const handler: StepHandler = {
  match: (tool) => tool.startsWith("gate:"),
  async run({ runId, step }) {
    const stepId = step.id;
    const gateName = step.tool.replace(/^gate:/, "");

    await query(`update nofx.step set status='running', started_at=now() where id=$1`, [stepId]);
    await recordEvent(runId, "step.started", { name: step.name, tool: step.tool }, stepId);

    const scriptPath = path.resolve(process.cwd(), "scripts", "runGate.js");
    const { gates } = await getSettings();

    // Skip if gate disabled by settings
    if ((gateName === 'typecheck' && !gates.typecheck) ||
        (gateName === 'lint' && !gates.lint) ||
        (gateName === 'unit' && !gates.unit)) {
      await query(`update nofx.step set status='succeeded', outputs=$2, ended_at=now() where id=$1`, [
        stepId,
        JSON.stringify({ gate: gateName, skipped: true })
      ]).catch(async () => {
        await query(`update nofx.step set status='succeeded', outputs=$2, completed_at=now() where id=$1`, [
          stepId,
          JSON.stringify({ gate: gateName, skipped: true })
        ]);
      });
      await recordEvent(runId, "step.finished", { gate: gateName, skipped: true }, stepId);
      return;
    }

    const proc = spawnSync(process.execPath, [scriptPath, gateName], {
      cwd: process.cwd(),
      encoding: "utf8",
      stdio: ["ignore", "pipe", "pipe"],
      env: { ...process.env, COVERAGE_THRESHOLD: String(gates.coverageThreshold ?? 0.9) }
    });

    let summary: any = { gate: gateName, passed: proc.status === 0 };
    try {
      const line = (proc.stdout || "").trim().split(/\n/).filter(Boolean).pop();
      if (line) {
        const parsed = JSON.parse(line);
        if (parsed && parsed.summary) summary = parsed.summary;
      }
    } catch (e) {
      // keep default summary
    }

    // Collect and upload evidence artifacts produced by the gate runner
    const localDir = path.resolve(process.cwd(), "gate-artifacts");
    const uploadedPaths: string[] = [];
    if (fs.existsSync(localDir)) {
      const files = fs.readdirSync(localDir);
      for (const f of files) {
        const full = path.join(localDir, f);
        if (!fs.statSync(full).isFile()) continue;
        const storagePath = `runs/${runId}/steps/${stepId}/gate-artifacts/${f}`;
        const fileBuf = fs.readFileSync(full);
        const { error } = await supabase.storage
          .from(ARTIFACT_BUCKET)
          .upload(storagePath, fileBuf as any, { upsert: true, contentType: contentTypeFor(f) } as any);
        if (!error) {
          uploadedPaths.push(storagePath);
          await query(
            `insert into nofx.artifact (step_id, type, uri, metadata) values ($1,$2,$3,$4)`,
            [stepId, contentTypeFor(f), storagePath, JSON.stringify({ gate: gateName })]
          ).catch(async () => {
            // fallback for schema with 'path' instead of 'uri'
            await query(
              `insert into nofx.artifact (step_id, type, path, metadata) values ($1,$2,$3,$4)`,
              [stepId, contentTypeFor(f), storagePath, JSON.stringify({ gate: gateName })]
            );
          });
        } else {
          log.error({ error, storagePath }, "gate artifact upload failed");
        }
      }
    }

    // Always upload a JSON summary as gate-summary.json
    const summaryName = "gate-summary.json";
    const summaryPath = `runs/${runId}/steps/${stepId}/${summaryName}`;
    const { error: summaryErr } = await supabase.storage
      .from(ARTIFACT_BUCKET)
      .upload(summaryPath, Buffer.from(JSON.stringify(summary, null, 2)), {
        upsert: true,
        contentType: "application/json",
      } as any);
    if (!summaryErr) {
      uploadedPaths.push(summaryPath);
      await query(
        `insert into nofx.artifact (step_id, type, uri, metadata) values ($1,$2,$3,$4)`,
        [stepId, "application/json", summaryPath, JSON.stringify({ gate: gateName, kind: "summary" })]
      ).catch(async () => {
        await query(
          `insert into nofx.artifact (step_id, type, path, metadata) values ($1,$2,$3,$4)`,
          [stepId, "application/json", summaryPath, JSON.stringify({ gate: gateName, kind: "summary" })]
        );
      });
    }

    if (summary.passed) {
      await query(`update nofx.step set status='succeeded', outputs=$2, ended_at=now() where id=$1`, [
        stepId,
        JSON.stringify({ gate: gateName, summary, artifacts: uploadedPaths })
      ]).catch(async () => {
        await query(`update nofx.step set status='succeeded', outputs=$2, completed_at=now() where id=$1`, [
          stepId,
          JSON.stringify({ gate: gateName, summary, artifacts: uploadedPaths })
        ]);
      });
      await recordEvent(runId, "step.finished", { gate: gateName, summary }, stepId);
    } else {
      await query(`update nofx.step set status='failed', outputs=$2, ended_at=now() where id=$1`, [
        stepId,
        JSON.stringify({ gate: gateName, summary, artifacts: uploadedPaths })
      ]).catch(async () => {
        await query(`update nofx.step set status='failed', outputs=$2, completed_at=now() where id=$1`, [
          stepId,
          JSON.stringify({ gate: gateName, summary, artifacts: uploadedPaths })
        ]);
      });
      await recordEvent(runId, "step.failed", { gate: gateName, summary, stderr: proc.stderr }, stepId);
      throw new Error(`gate ${gateName} failed`);
    }
  }
};

export default handler;

import { StepHandler } from "./types";
import { query } from "../../lib/db";
import { recordEvent } from "../../lib/events";
import { supabase, ARTIFACT_BUCKET } from "../../lib/supabase";
import { log } from "../../lib/logger";
import { spawnSync } from "node:child_process";
import fs from "node:fs";
import path from "node:path";

type CommitItem = {
  path: string;               // repo-relative file path
  fromArtifact?: string;      // Supabase storage path of artifact (direct)
  fromStep?: string;          // resolve artifact by prior step name
  artifactName?: string;      // filename within that step (e.g., README.md)
  content?: string;           // inline content
  mode?: 'overwrite';
};
type Inputs = {
  branch?: string;
  base?: string;              // base branch (default: main)
  title?: string;
  body?: string;
  draft?: boolean;
  commits: CommitItem[];
};

function sh(cmd: string, args: string[], cwd: string){
  const p = spawnSync(cmd, args, { cwd, stdio: 'pipe', encoding: 'utf8' });
  if (p.status !== 0) throw new Error(`cmd failed: ${cmd} ${args.join(' ')}\n${p.stderr}`);
  return p.stdout.trim();
}
function repoRoot(){ return process.cwd(); }
function ensureDir(filePath:string){ fs.mkdirSync(path.dirname(filePath), { recursive: true }); }

async function getArtifactBuffer(pth: string): Promise<Buffer> {
  const { data, error } = await supabase.storage.from(ARTIFACT_BUCKET).download(pth);
  if (error || !data) throw new Error(`artifact not found: ${pth}`);
  const arr = await data.arrayBuffer();
  return Buffer.from(arr);
}

function parseOrigin(url: string){
  // supports git@github.com:owner/repo.git or https://github.com/owner/repo.git
  let m = url.match(/github.com[:/](.+?)\/(.+?)(?:\.git)?$/);
  if (!m) throw new Error('unsupported origin url: ' + url);
  return { owner: m[1], repo: m[2] };
}

const handler: StepHandler = {
  match: (tool) => tool === 'git_pr',
  async run({ runId, step }) {
    const stepId = step.id;
    await query(`update nofx.step set status='running', started_at=now() where id=$1`, [stepId])
      .catch(async ()=>{ await query(`update nofx.step set status='running' where id=$1`, [stepId]); });
    await recordEvent(runId, 'step.started', { name: step.name, tool: step.tool }, stepId);

    const inputs: Inputs = step.inputs || {};
    if (!Array.isArray(inputs.commits) || inputs.commits.length === 0) throw new Error('git_pr requires commits');
    const base = inputs.base || process.env.GIT_DEFAULT_BASE || 'main';
    const branch = inputs.branch || `feat/run-${runId.slice(0,8)}`;
    const repo = repoRoot();

    // prepare files
    for (const c of inputs.commits) {
      const outPath = path.join(repo, c.path);
      ensureDir(outPath);
      if (c.fromArtifact) {
        const buf = await getArtifactBuffer(c.fromArtifact);
        fs.writeFileSync(outPath, buf);
      } else if (c.fromStep && c.artifactName) {
        // resolve artifact by step name and filename
        const stepRow = await query<any>(`select id from nofx.step where run_id=$1 and name=$2 limit 1`, [runId, c.fromStep]);
        const sid = stepRow.rows[0]?.id;
        if (!sid) throw new Error(`step not found: ${c.fromStep}`);
        const art = await query<any>(`select coalesce(uri,path) as uri from nofx.artifact where step_id=$1 and (uri like $2 or path like $2) limit 1`, [sid, `%/${c.artifactName}`]);
        const pth = art.rows[0]?.uri;
        if (!pth) throw new Error(`artifact not found: ${c.artifactName} in step ${c.fromStep}`);
        const buf = await getArtifactBuffer(pth);
        fs.writeFileSync(outPath, buf);
      } else if (typeof c.content === 'string') {
        fs.writeFileSync(outPath, c.content, 'utf8');
      } else {
        throw new Error('commit item requires fromArtifact or content');
      }
    }

    // git plumbing
    try { sh('git', ['rev-parse','--is-inside-work-tree'], repo); } catch { throw new Error('not a git repo'); }
    sh('git', ['fetch','origin', base], repo);
    // Create branch from origin/base
    sh('git', ['checkout','-B', branch, `origin/${base}`], repo);
    sh('git', ['add', '--all'], repo);
    const commitMsg = inputs.title || `Update by NOFX run ${runId}`;
    // set identity if needed
    try { sh('git', ['config','user.email'], repo); } catch { sh('git', ['config','user.email', 'nofx@example.com'], repo); }
    try { sh('git', ['config','user.name'], repo); } catch { sh('git', ['config','user.name', 'NOFX Bot'], repo); }
    sh('git', ['commit','-m', commitMsg], repo);
    sh('git', ['push','-u','origin', branch], repo);

    // Open PR via GitHub API
    let prUrl: string | undefined;
    try {
      const origin = sh('git', ['config','--get','remote.origin.url'], repo);
      const { owner, repo: name } = parseOrigin(origin);
      const token = process.env.GITHUB_TOKEN || process.env.GH_TOKEN;
      if (!token) throw new Error('GITHUB_TOKEN not set');
      const rsp = await fetch(`https://api.github.com/repos/${owner}/${name}/pulls`, {
        method: 'POST',
        headers: { 'Authorization': `token ${token}`, 'Content-Type': 'application/json', 'Accept': 'application/vnd.github+json' },
        body: JSON.stringify({
          title: inputs.title || commitMsg,
          head: branch,
          base,
          body: inputs.body || `Automated PR from NOFX run ${runId}`,
          draft: !!inputs.draft
        })
      } as any);
      if ((rsp as any).ok) {
        const data: any = await (rsp as any).json();
        prUrl = data.html_url;
      } else {
        const t = await (rsp as any).text();
        throw new Error(`create PR failed: ${t}`);
      }
    } catch (e:any) {
      log.warn({ err: e?.message }, 'PR creation failed; commit pushed');
    }

    const outputs = { branch, base, prUrl, files: inputs.commits.map(c=>c.path) };
    await query(`update nofx.step set status='succeeded', outputs=$2, ended_at=now() where id=$1`, [
      stepId, JSON.stringify(outputs)
    ]).catch(async ()=>{
      await query(`update nofx.step set status='succeeded', outputs=$2, completed_at=now() where id=$1`, [
        stepId, JSON.stringify(outputs)
      ]);
    });
    await recordEvent(runId, 'step.finished', outputs, stepId);
  }
};

export default handler;

