import fs from 'node:fs';
import fsp from 'node:fs/promises';
import path from 'node:path';
import { spawnSync } from 'node:child_process';
import { store } from './store';
import { supabase, ARTIFACT_BUCKET } from './supabase';
import { query } from './db';

export type BackupMeta = {
  id: string;
  created_at: string;
  title: string;
  note?: string;
  size_bytes?: number;
  kind: 'fs'|'db';
  scope?: BackupScope;
  cloud?: { uploaded?: boolean; path?: string; error?: string };
};

export type BackupScope = 'data'|'with-project'|'project-only';

const ROOT = path.join(process.cwd(), 'local_data');
const BACKUP_DIR = path.join(ROOT, 'backups');

function ensureDir(p: string){ if (!fs.existsSync(p)) fs.mkdirSync(p, { recursive: true }); }

export async function listBackups(): Promise<BackupMeta[]> {
  ensureDir(BACKUP_DIR);
  const metas: BackupMeta[] = [];
  const files = await fsp.readdir(BACKUP_DIR).catch(()=>[] as string[]);
  for (const f of files) {
    if (!f.endsWith('.json')) continue;
    try {
      const j = JSON.parse(await fsp.readFile(path.join(BACKUP_DIR, f), 'utf8')) as BackupMeta;
      metas.push(j);
    } catch {}
  }
  metas.sort((a,b)=> (a.created_at < b.created_at ? 1 : -1));
  return metas;
}

function safeSlug(s: string){ return s.toLowerCase().replace(/[^a-z0-9]+/g,'-').replace(/^-+|-+$/g,'').slice(0,40); }

export async function createBackup(note?: string, scope: BackupScope = 'data'): Promise<BackupMeta> {
  ensureDir(BACKUP_DIR);
  const now = new Date().toISOString();
  const latestList = await store.listRuns(1);
  const latest = latestList && latestList.length ? (latestList[0] as { id: string; title?: string }) : undefined;
  let title = latest?.title || 'auto backup';
  if (!title && latest?.id) title = `run ${latest.id}`;
  const id = `${now.replace(/[:.]/g,'-')}-${safeSlug(title || 'nofx') || 'nofx'}`;
  const kind: 'fs'|'db' = (store.driver === 'db') ? 'db' : 'fs';
  const metaPath = path.join(BACKUP_DIR, `${id}.json`);
  const tarPath = path.join(BACKUP_DIR, `${id}.tar.gz`);

  const tmp = path.join(BACKUP_DIR, `.tmp-${id}`);
  ensureDir(tmp);
  if (kind === 'fs') {
    // stage NOFX data
    if (scope !== 'project-only') {
      const dataDst = path.join(tmp, 'nofx_data');
      await copyDir(ROOT, dataDst, ['backups']);
    }
  } else {
    // stage DB export
    if (scope !== 'project-only') {
      const dump: Record<string, unknown> = {};
      try {
        const tables = ['nofx.run','nofx.step','nofx.event','nofx.gate','nofx.artifact','nofx.settings','nofx.model'];
        for (const t of tables) {
          try {
            const rows = await query<Record<string, unknown>>(`select * from ${t}`);
            dump[t] = rows.rows;
          } catch {}
        }
        await fsp.writeFile(path.join(tmp, 'db.json'), JSON.stringify(dump, null, 2));
      } catch {}
    }
  }
  // optionally include the project working tree
  if (scope === 'with-project' || scope === 'project-only') {
    const projDst = path.join(tmp, 'project');
    await copyDir(process.cwd(), projDst, ['node_modules','\.git','local_data/backups','coverage','test-results','trash']);
  }
  // tar the staged tmp
  const res = spawnSync('tar', ['-czf', tarPath, '-C', tmp, '.']);
  if (res.status !== 0) throw new Error('tar failed');
  try { await fsp.rm(tmp, { recursive: true, force: true }); } catch {}
  const stat = await fsp.stat(tarPath).catch(()=>({ size: 0 } as fs.Stats));
  const meta: BackupMeta = { id, created_at: now, title, note, size_bytes: stat.size, kind, scope };

  // Try cloud upload to Supabase Storage if configured
  try {
    const u8 = await fsp.readFile(tarPath);
    const key = `backups/${path.basename(tarPath)}`;
    const { error } = await supabase.storage.from(ARTIFACT_BUCKET).upload(key, new Blob([u8]), { upsert: true, contentType: 'application/gzip' });
    meta.cloud = error ? { uploaded: false, error: error.message } : { uploaded: true, path: key };
  } catch (e: unknown) {
    const msg = e instanceof Error ? e.message : String(e);
    meta.cloud = { uploaded: false, error: msg };
  }

  await fsp.writeFile(metaPath, JSON.stringify(meta, null, 2));
  return meta;
}

export async function restoreBackup(id: string): Promise<BackupMeta> {
  ensureDir(BACKUP_DIR);
  const metaPath = path.join(BACKUP_DIR, `${id}.json`);
  const tarPath = path.join(BACKUP_DIR, `${id}.tar.gz`);
  const meta = JSON.parse(await fsp.readFile(metaPath, 'utf8')) as BackupMeta;
  if (!fs.existsSync(tarPath)) throw new Error('snapshot missing tar');
  // Always snapshot current state before restore
  await createBackup(`auto-pre-restore:${id}`);
  const tmp = path.join(BACKUP_DIR, `.restore-${id}`);
  ensureDir(tmp);
  const ext = spawnSync('tar', ['-xzf', tarPath, '-C', tmp]);
  if (ext.status !== 0) throw new Error('restore extract failed');
  if (meta.kind === 'fs') {
    // Restore local_data from staged nofx_data if present
    const staged = path.join(tmp, 'nofx_data');
    if (fs.existsSync(staged)) {
      await copyDir(staged, ROOT, []);
    }
    try { await fsp.rm(tmp, { recursive: true, force: true }); } catch {}
    return meta;
  }
  // DB restore: read db.json and import
  const dbFile = path.join(tmp, 'db.json');
  if (!fs.existsSync(dbFile)) throw new Error('db.json not found in snapshot');
  const dump = JSON.parse(await fsp.readFile(dbFile, 'utf8')) as Record<string, Record<string, unknown>[]>;
  await restoreDbFromJson(dump);
  try { await fsp.rm(tmp, { recursive: true, force: true }); } catch {}
  return meta;
}

async function restoreDbFromJson(dump: Record<string, Record<string, unknown>[]>) {
  // Order matters due to FKs; delete children first
  const delOrder = ['nofx.artifact','nofx.event','nofx.gate','nofx.step','nofx.run','nofx.model','nofx.settings'];
  const insOrder = ['nofx.settings','nofx.model','nofx.run','nofx.step','nofx.artifact','nofx.event','nofx.gate'];
  await query('begin');
  try {
    for (const t of delOrder) { try { await query(`delete from ${t}`); } catch {} }
    for (const t of insOrder) {
      const rows = dump[t] || [];
      if (!rows.length) continue;
      // Build dynamic insert
      const cols = Object.keys(rows[0]);
      const placeholders: string[] = [];
      const values: unknown[] = [];
      const chunk = 100;
      for (let i=0;i<rows.length;i+=chunk){
        const part = rows.slice(i, i+chunk);
        placeholders.length = 0; values.length = 0;
        part.forEach((r, idx) => {
          const ps = cols.map((_, cidx) => `$${idx*cols.length + cidx + 1}`);
          placeholders.push(`(${ps.join(',')})`);
          values.push(...cols.map(k => r[k]));
        });
        const sql = `insert into ${t} (${cols.map(c=>`"${c}"`).join(',')}) values ${placeholders.join(',')}`;
        await query(sql, values).catch(()=>{});
      }
    }
    await query('commit');
  } catch (e) {
    await query('rollback').catch(()=>{});
    throw e;
  }
}

async function copyDir(src: string, dst: string, exclude: string[]) {
  ensureDir(dst);
  const ents = await fsp.readdir(src, { withFileTypes: true });
  for (const ent of ents) {
    const name = ent.name;
    if (exclude.some(pat => {
      const p = String(pat).replace(/\\\./g, '.');
      return p === name;
    })) continue;
    const s = path.join(src, name);
    const d = path.join(dst, name);
    if (ent.isDirectory()) {
      await copyDir(s, d, exclude);
    } else if (ent.isFile()) {
      await fsp.mkdir(path.dirname(d), { recursive: true });
      await fsp.copyFile(s, d);
    }
  }
}

import fs from 'node:fs';
import fsp from 'node:fs/promises';
import path from 'node:path';
import crypto from 'node:crypto';

const ROOT = path.join(process.cwd(), 'local_data', 'cache');

function ensureDirSync(p: string) {
  if (!fs.existsSync(p)) fs.mkdirSync(p, { recursive: true });
}

function fileFor(ns: string, key: string) {
  const safeNs = ns.replace(/[^a-z0-9_.:-]/gi, '_');
  const h = crypto.createHash('sha256').update(key).digest('hex');
  const dir = path.join(ROOT, safeNs);
  ensureDirSync(dir);
  return path.join(dir, `${h}.json`);
}

export async function getCacheJSON<T=any>(ns: string, key: string): Promise<T | null> {
  try {
    const file = fileFor(ns, key);
    const s = await fsp.readFile(file, 'utf8');
    const data = JSON.parse(s);
    const exp = Number(data?.expiresAt || 0);
    if (exp && Date.now() > exp) {
      // expired; best-effort unlink
      try { await fsp.unlink(file); } catch {}
      return null;
    }
    return data?.value ?? null;
  } catch { return null; }
}

export async function setCacheJSON(ns: string, key: string, value: any, ttlMs: number): Promise<void> {
  const file = fileFor(ns, key);
  const expiresAt = Date.now() + Math.max(0, Number(ttlMs || 0));
  const data = { expiresAt, value };
  await fsp.writeFile(file, JSON.stringify(data));
}

export async function invalidateNamespace(ns: string): Promise<number> {
  const dir = path.join(ROOT, ns);
  try {
    const files = await fsp.readdir(dir);
    let n = 0;
    for (const f of files) {
      try { await fsp.unlink(path.join(dir, f)); n++; } catch {}
    }
    return n;
  } catch { return 0; }
}

import { Pool } from "pg";
import dotenv from "dotenv";
dotenv.config();
import { metrics } from './metrics';
import { log } from './logger';

export const pool = new Pool({
  connectionString: process.env.DATABASE_URL
});

export async function query<T = any>(text: string, params?: any[]) {
  const start = Date.now();
  try {
    const res = await pool.query(text, params);
    const latencyMs = Date.now() - start;
    try { metrics.dbQueryDuration.observe({ op: 'query' }, latencyMs); } catch {}
    // Avoid logging SQL text to prevent leaking sensitive data
    log.debug({ status: 'ok', latencyMs }, 'db.query');
    return res as { rows: T[] };
  } catch (err) {
    const latencyMs = Date.now() - start;
    try { metrics.dbQueryDuration.observe({ op: 'query' }, latencyMs); } catch {}
    log.error({ status: 'error', latencyMs, err }, 'db.query.error');
    throw err;
  }
}

import { store } from "./store";

export async function recordEvent(runId: string, type: string, payload: any = {}, stepId?: string) {
  await store.recordEvent(runId, type, payload, stepId);
}

import { query } from './db';

export type ModelRow = {
  id: string;
  tenant_id: string;
  name: string;
  display_name?: string;
  provider: string;
  kind: string;
  base_url?: string;
  input_per_1m?: number;
  output_per_1m?: number;
  context_tokens?: number;
  max_output_tokens?: number;
  active: boolean;
  metadata?: any;
};

export async function listModels(): Promise<ModelRow[]> {
  const r = await query<ModelRow>(`select * from nofx.model where tenant_id='local' order by provider, name`);
  return r.rows as any;
}
export async function getModelByName(name: string): Promise<ModelRow | undefined> {
  const r = await query<ModelRow>(`select * from nofx.model where tenant_id='local' and name=$1 limit 1`, [name]);
  return r.rows[0] as any;
}
export async function upsertModel(m: Partial<ModelRow>): Promise<ModelRow> {
  const r = await query<ModelRow>(
    `insert into nofx.model (tenant_id, name, display_name, provider, kind, base_url, input_per_1m, output_per_1m, context_tokens, max_output_tokens, active, metadata)
     values ('local', $1,$2,$3,$4,$5,$6,$7,$8,$9, coalesce($10,true), coalesce($11,'{}'::jsonb))
     on conflict (tenant_id, provider, name) do update set display_name=excluded.display_name, kind=excluded.kind, base_url=excluded.base_url, input_per_1m=excluded.input_per_1m, output_per_1m=excluded.output_per_1m, context_tokens=excluded.context_tokens, max_output_tokens=excluded.max_output_tokens, active=excluded.active, metadata=excluded.metadata
     returning *`,
    [m.name, m.display_name || null, m.provider, m.kind || 'openai', m.base_url || null, m.input_per_1m || null, m.output_per_1m || null, m.context_tokens || null, m.max_output_tokens || null, m.active ?? true, m.metadata || {}]
  );
  return r.rows[0] as any;
}
export async function deleteModel(id: string): Promise<void> {
  await query(`delete from nofx.model where id=$1 and tenant_id='local'`, [id]);
}

import { log } from "../logger";
import { metrics } from "../metrics";

type Job = { id: number; payload: any; runAt: number; enqueuedAt: number };

export class MemoryQueueAdapter {
  private subs = new Map<string, Array<(p:any)=>Promise<void>>>();
  private queues = new Map<string, Job[]>();
  private active = new Map<string, number>();
  private counts = new Map<string, { waiting:number; active:number; completed:number; failed:number; delayed:number; paused:number }>();
  private idSeq = 1;
  private dlq = new Map<string, Job[]>();
  private readonly backoffScheduleMs = [0, 2000, 5000, 10000];
  private maxConcurrent = Math.max(1, Number(process.env.WORKER_CONCURRENCY || process.env.NOFX_WORKER_CONCURRENCY || 4));

  private ensure(topic: string) {
    if (!this.queues.has(topic)) this.queues.set(topic, []);
    if (!this.counts.has(topic)) this.counts.set(topic, { waiting: 0, active: 0, completed: 0, failed: 0, delayed: 0, paused: 0 });
    if (!this.active.has(topic)) this.active.set(topic, 0);
  }

  private updateGauges(topic: string) {
    try {
      const c = this.counts.get(topic)!;
      metrics.queueDepth.set({ topic, state: 'waiting' }, c.waiting);
      metrics.queueDepth.set({ topic, state: 'active' }, c.active);
      metrics.queueDepth.set({ topic, state: 'completed' }, c.completed);
      metrics.queueDepth.set({ topic, state: 'failed' }, c.failed);
      metrics.queueDepth.set({ topic, state: 'delayed' }, c.delayed);
      metrics.queueDepth.set({ topic, state: 'paused' }, c.paused);
      const dlqSize = (this.dlq.get(topic) || []).length;
      metrics.dlqSize.set({ topic }, dlqSize);
      const oldest = this.getOldestAgeMs(topic) || 0;
      metrics.queueOldestAgeMs.set({ topic }, oldest);
    } catch {}
  }

  async enqueue(topic: string, payload: any, options?: { delay?: number }) {
    this.ensure(topic);
    const delay = Math.max(0, Number(options?.delay || 0));
    const now = Date.now();
    const runAt = now + delay;
    const job: Job = { id: this.idSeq++, payload, runAt, enqueuedAt: now };
    const q = this.queues.get(topic)!;
    q.push(job);
    const c = this.counts.get(topic)!;
    if (delay > 0) c.delayed += 1; else c.waiting += 1;
    log.info({ topic, payload, delay }, 'memq.enqueued');
    this.updateGauges(topic);
    setTimeout(() => this.drain(topic), Math.max(0, runAt - Date.now()));
  }

  subscribe(topic: string, handler: (payload:any)=>Promise<void>) {
    const arr = this.subs.get(topic) || [];
    arr.push(handler);
    this.subs.set(topic, arr);
    log.info({ topic }, 'memq.subscribed');
    // kick off any waiting jobs
    this.drain(topic);
  }

  private async drain(topic: string) {
    this.ensure(topic);
    const subs = this.subs.get(topic) || [];
    if (subs.length === 0) return;
    const handler = subs[0];
    const q = this.queues.get(topic)!;
    const c = this.counts.get(topic)!;
    const now = Date.now();

    // Launch up to maxConcurrent jobs that are ready
    while (true) {
      const active = this.active.get(topic) || 0;
      if (active >= this.maxConcurrent) break;
      const readyIdx = q.findIndex(j => j.runAt <= now);
      if (readyIdx === -1) break;
      const job = q.splice(readyIdx, 1)[0];
      if (job.runAt > now) { c.delayed = Math.max(0, c.delayed - 1); } else { c.waiting = Math.max(0, c.waiting - 1); }
      this.active.set(topic, active + 1);
      c.active += 1;
      this.updateGauges(topic);
      (async () => {
        try {
          await handler(job.payload);
          c.completed += 1;
          log.info({ topic, jobId: job.id, status: 'completed' }, 'memq.completed');
        } catch (err) {
          c.failed += 1;
          log.error({ topic, jobId: job.id, status: 'failed', err }, 'memq.failed');
          // retry with backoff schedule; on max, move to DLQ
          const attempt = Number(job.payload?.__attempt || 1);
          const nextIndex = attempt; // index into backoffSchedule for next delay
          const nextDelay = this.backoffScheduleMs[nextIndex];
          if (Number.isFinite(nextDelay)) {
            const nextPayload = { ...job.payload, __attempt: attempt + 1 };
            await this.enqueue(topic, nextPayload, { delay: nextDelay });
            try { metrics.retriesTotal.inc({ provider: String(job.payload?.provider || 'queue') }); } catch {}
          } else {
            const dlqTopic = (topic === 'step.ready') ? 'step.dlq' : `${topic}.dlq`;
            const dlqs = this.dlq.get(dlqTopic) || [];
            dlqs.push({ id: this.idSeq++, payload: job.payload, runAt: Date.now(), enqueuedAt: Date.now() } as Job);
            this.dlq.set(dlqTopic, dlqs);
            log.warn({ topic, jobId: job.id, attempts: attempt, dlqTopic }, 'memq.to_dlq');
          }
        } finally {
          c.active = Math.max(0, c.active - 1);
          this.active.set(topic, Math.max(0, (this.active.get(topic) || 1) - 1));
          this.updateGauges(topic);
          if (this.queues.get(topic)!.length) setImmediate(() => this.drain(topic));
        }
      })();
    }
  }

  async getCounts(topic: string) {
    this.ensure(topic);
    return this.counts.get(topic)!;
  }

  hasSubscribers(topic: string): boolean {
    const arr = this.subs.get(topic) || [];
    return arr.length > 0;
  }

  async listDlq(topic: string) {
    return (this.dlq.get(topic) || []).map(j => j.payload);
  }
  async rehydrateDlq(topic: string, max = 50) {
    const arr = this.dlq.get(topic) || [];
    const take = arr.splice(0, Math.max(0, Math.min(max, arr.length)));
    this.dlq.set(topic, arr);
    for (const job of take) {
      const payload = { ...job.payload, __attempt: 1 };
      const from = topic.endsWith('.dlq') ? topic.replace(/\.dlq$/, '.ready') : topic;
      await this.enqueue(from, payload, { delay: 0 });
    }
    return take.length;
  }

  /** Oldest waiting job age in ms (excludes delayed jobs not yet due). */
  getOldestAgeMs(topic: string): number | null {
    this.ensure(topic);
    const q = this.queues.get(topic)!;
    const now = Date.now();
    let oldest: number | null = null;
    for (const j of q) {
      if (j.runAt > now) continue; // not yet due
      const age = now - j.enqueuedAt;
      if (oldest == null || age > oldest) oldest = age;
    }
    return oldest;
  }
}

import { Queue, Worker, JobsOptions, Job } from "bullmq";
import IORedis from "ioredis";
import { log } from "../logger";
import { metrics } from "../metrics";

export class RedisQueueAdapter {
  connection = new IORedis(process.env.REDIS_URL || "redis://localhost:6379", {
    maxRetriesPerRequest: null
  });
  queues = new Map<string, Queue>();
  private readonly backoffScheduleMs = [0, 2000, 5000, 10000];
  private readonly DLQ_TOPIC = 'step.dlq';

  private getQueue(topic: string) {
    if (!this.queues.has(topic)) {
      const q = new Queue(topic, { connection: this.connection });
      this.queues.set(topic, q);
    }
    return this.queues.get(topic)!;
  }
  private async updateGauges(topic: string) {
    try {
      const q = this.getQueue(topic);
      const c = await q.getJobCounts('waiting','active','completed','failed','delayed','paused');
      metrics.queueDepth.set({ topic, state: 'waiting' }, c.waiting || 0);
      metrics.queueDepth.set({ topic, state: 'active' }, c.active || 0);
      metrics.queueDepth.set({ topic, state: 'completed' }, c.completed || 0);
      metrics.queueDepth.set({ topic, state: 'failed' }, c.failed || 0);
      metrics.queueDepth.set({ topic, state: 'delayed' }, c.delayed || 0);
      metrics.queueDepth.set({ topic, state: 'paused' }, c.paused || 0);
      metrics.dlqSize.set({ topic }, c.failed || 0);
      // Oldest waiting job age (approximate, scans up to 20 waiting jobs)
      try {
        const now = Date.now();
        const waiting = await (q as any).getWaiting(0, 19) as Job[];
        let oldestTs: number | null = null;
        for (const j of waiting) {
          const ts = (j as any).timestamp as number | undefined;
          if (typeof ts === 'number') {
            if (oldestTs == null || ts < oldestTs) oldestTs = ts;
          }
        }
        const age = oldestTs != null ? Math.max(0, now - oldestTs) : 0;
        metrics.queueOldestAgeMs.set({ topic }, age);
      } catch {}
    } catch {}
  }
  async enqueue(topic: string, payload: any, options?: JobsOptions) {
    await this.getQueue(topic).add("job", payload, options);
    log.info({ topic, payload }, "enqueued");
    this.updateGauges(topic);
  }
  subscribe(topic: string, handler: (payload:any)=>Promise<void>) {
    const concurrency = Math.max(1, Number(process.env.WORKER_CONCURRENCY || process.env.NOFX_WORKER_CONCURRENCY || 1));
    const w = new Worker(topic, async (job) => {
      await handler(job.data);
    }, { connection: this.connection, concurrency });
    w.on('ready', () => { log.info({ topic }, 'worker.ready'); this.updateGauges(topic); });
    w.on('active', (job) => { log.info({ topic, jobId: job.id }, 'worker.active'); this.updateGauges(topic); });
    w.on('completed', (job) => { log.info({ topic, jobId: job.id, status: 'completed' }, 'worker.completed'); this.updateGauges(topic); });
    w.on('failed', async (job, err) => {
      log.error({ topic, jobId: job?.id, status: 'failed', err }, 'worker.failed');
      try {
        if (!job) return;
        const data = job.data || {};
        const attempt = Number(data.__attempt || 1);
        const nextDelay = this.backoffScheduleMs[attempt];
        if (Number.isFinite(nextDelay)) {
          const nextPayload = { ...data, __attempt: attempt + 1 };
          await this.getQueue(topic).add('job', nextPayload, { delay: nextDelay });
          try { metrics.retriesTotal.inc({ provider: String(data?.provider || 'queue') }); } catch {}
        } else {
          await this.getQueue(this.DLQ_TOPIC).add('job', data, { delay: 0 });
          log.warn({ topic, jobId: job.id, attempts: attempt }, 'redis.to_dlq');
        }
      } catch (e) {
        log.error({ e }, 'redis.failed-handler.error');
      }
      this.updateGauges(topic);
    });
    log.info({ topic }, "subscribed");
  }

  async getCounts(topic: string) {
    const q = this.getQueue(topic);
    return q.getJobCounts('waiting','active','completed','failed','delayed','paused');
  }

  constructor() {
    this.connection.on('connect', () => log.info('redis.connect'));
    this.connection.on('ready', () => log.info('redis.ready'));
    this.connection.on('error', (err) => log.error({ err }, 'redis.error'));
    this.connection.on('reconnecting', () => log.warn('redis.reconnecting'));
    this.connection.on('end', () => log.warn('redis.end'));
  }

  async listDlq(topic: string) {
    const q = this.getQueue(topic);
    const jobs: Job[] = await q.getJobs(['waiting','delayed']);
    return jobs.map(j => j.data);
  }
  async rehydrateDlq(topic: string, max = 50) {
    const q = this.getQueue(topic);
    const jobs: Job[] = await q.getJobs(['waiting','delayed']);
    const take = jobs.slice(0, max);
    let n = 0;
    for (const j of take) {
      await this.getQueue('step.ready').add('job', { ...j.data, __attempt: 1 }, { delay: 0 });
      await j.remove();
      n += 1;
    }
    return n;
  }

  // Not easily available without scanning Redis sorted sets; return null for now
  getOldestAgeMs(_topic: string): number | null {
    return null;
  }
}

