import { log } from './logger';

let initialized = false;

export async function initTracing(serviceName: string) {
  if (initialized) return;
  if (process.env.OTEL_ENABLED !== '1') {
    return; // disabled
  }
  try {
    // Dynamic imports keep runtime optional when not installed
    const { NodeSDK } = await import('@opentelemetry/sdk-node' as any);
    const { Resource } = await import('@opentelemetry/resources' as any);
    const { SemanticResourceAttributes } = await import('@opentelemetry/semantic-conventions' as any);
    const { OTLPTraceExporter } = await import('@opentelemetry/exporter-trace-otlp-http' as any);
    const { HttpInstrumentation } = await import('@opentelemetry/instrumentation-http' as any);
    const { ExpressInstrumentation } = await import('@opentelemetry/instrumentation-express' as any);
    const { IORedisInstrumentation } = await import('@opentelemetry/instrumentation-ioredis' as any);
    const { PgInstrumentation } = await import('@opentelemetry/instrumentation-pg' as any);

    const exporter = new (OTLPTraceExporter as any)({
      // If OTEL_EXPORTER_OTLP_ENDPOINT set, sdk will honor it; leave defaults
    });
    const sdk = new (NodeSDK as any)({
      resource: new (Resource as any)({
        [SemanticResourceAttributes.SERVICE_NAME]: serviceName || 'nofx-local',
        [SemanticResourceAttributes.DEPLOYMENT_ENVIRONMENT]: process.env.NODE_ENV || 'development'
      }),
      traceExporter: exporter,
      instrumentations: [
        new (HttpInstrumentation as any)(),
        new (ExpressInstrumentation as any)(),
        new (IORedisInstrumentation as any)(),
        new (PgInstrumentation as any)(),
      ]
    });
    await sdk.start();
    initialized = true;
    log.info('otel.tracing.started');
  } catch (e) {
    log.warn({ err: (e as any)?.message }, 'otel.tracing.disabled');
  }
}

import Anthropic from '@anthropic-ai/sdk';
export async function claudeChat(prompt: string, model=process.env.ANTHROPIC_MODEL || 'claude-3-5-sonnet-latest', maxOutputTokens: number = 800){
  const client = new Anthropic({ apiKey: process.env.ANTHROPIC_API_KEY! });
  const started = Date.now();
  const rsp = await client.messages.create({ model, max_tokens: Math.max(1, Number(maxOutputTokens || 800)), messages:[{role:'user', content: prompt}]});
  const txt = (rsp.content[0] as any)?.text || '';
  const usage = (rsp as any).usage ? {
    inputTokens: (rsp as any).usage.input_tokens,
    outputTokens: (rsp as any).usage.output_tokens,
    totalTokens: ((rsp as any).usage.input_tokens || 0) + ((rsp as any).usage.output_tokens || 0),
    latencyMs: Date.now() - started
  } : undefined;
  return { text: txt.trim(), provider: 'anthropic', model, usage };
}

import { GoogleGenerativeAI } from '@google/generative-ai';
export async function geminiChat(prompt: string, model=process.env.GEMINI_MODEL || 'gemini-2.5-pro'){
  const genai = new GoogleGenerativeAI(process.env.GEMINI_API_KEY!);
  const modelRef = genai.getGenerativeModel({ model });
  const started = Date.now();
  const rsp = await modelRef.generateContent(prompt);
  const text = rsp.response.text().trim();
  const um: any = (rsp.response as any).usageMetadata || {};
  const usage = (um.promptTokenCount || um.candidatesTokenCount) ? {
    inputTokens: um.promptTokenCount,
    outputTokens: um.candidatesTokenCount,
    totalTokens: (um.totalTokenCount) || (um.promptTokenCount || 0) + (um.candidatesTokenCount || 0),
    latencyMs: Date.now() - started
  } : undefined;
  return { text, provider: 'gemini', model, usage };
}

import OpenAI from 'openai';
export async function openaiChat(
  prompt: string,
  model = process.env.OPENAI_MODEL || 'gpt-4o-mini',
  opts?: { baseURL?: string; apiKeyEnv?: string; maxOutputTokens?: number }
) {
  const apiKey = opts?.apiKeyEnv ? (process.env[opts.apiKeyEnv] as string | undefined) : process.env.OPENAI_API_KEY!;
  const client = new OpenAI({ apiKey: apiKey!, baseURL: opts?.baseURL });
  const started = Date.now();

  // Use Responses API for new models that don't support max_tokens on chat.completions
  // Build minimal payload for broadest compatibility across models
  const payload: any = { model, input: prompt, max_output_tokens: Math.max(1, Number(opts?.maxOutputTokens ?? 800)) };
  // Only include temperature when explicitly allowed via env
  if (process.env.OPENAI_ALLOW_TEMPERATURE === '1') payload.temperature = 0.2;
  let rsp: any;
  try {
    rsp = await client.responses.create(payload);
  } catch (e: any) {
    const msg = String(e?.message || '');
    const unsupported = /Unsupported parameter|not supported with this model/i.test(msg);
    if (!unsupported) throw e;
    // Fallback to Chat Completions for older/edge models
    const cc = await (client as any).chat.completions.create({
      model,
      messages: [{ role: 'user', content: prompt }],
      max_tokens: Math.max(1, Number(opts?.maxOutputTokens ?? 800))
    });
    const text = (cc as any).choices?.[0]?.message?.content?.trim?.() ?? '';
    const usage = cc.usage
      ? {
          inputTokens: cc.usage.prompt_tokens,
          outputTokens: cc.usage.completion_tokens,
          totalTokens: cc.usage.total_tokens,
          latencyMs: Date.now() - started
        }
      : undefined;
    return { text, provider: 'openai', model, usage };
  }

  // Extract text and usage in a version-tolerant way
  const text = (rsp as any).output_text
    || ((rsp as any).choices?.[0]?.message?.content?.trim?.() ?? '')
    || '';

  const u: any = (rsp as any).usage || {};
  const usage = u
    ? {
        inputTokens: u.input_tokens ?? u.prompt_tokens,
        outputTokens: u.output_tokens ?? u.completion_tokens,
        totalTokens: u.total_tokens,
        latencyMs: Date.now() - started
      }
    : undefined;

  return { text, provider: 'openai', model, usage };
}

import { openaiChat } from './providers/openai';
import { claudeChat } from './providers/anthropic';
import { geminiChat } from './providers/gemini';
import { getSettings } from '../lib/settings';
import { httpChat } from './providers/http';
import { getModelByName } from '../lib/models';
import { metrics } from '../lib/metrics';
import { setContext } from '../lib/observability';
import { getCacheJSON, setCacheJSON } from '../lib/cache';
import crypto from 'node:crypto';

export type TaskKind = 'codegen'|'reasoning'|'docs';
export type RouteOpts = { maxOutputTokens?: number };

export async function routeLLM(kind: TaskKind, prompt: string, opts?: RouteOpts){
  const { llm } = await getSettings();
  const modelOrder = llm?.modelOrder?.[kind as 'docs'|'reasoning'|'codegen'] || [];
  let lastErr: any;
  if (modelOrder.length) {
    for (const name of modelOrder) {
      try {
        const m = await getModelByName(name);
        if (!m || m.active === false) continue;
        const cached = await maybeGetDocsCache(kind, prompt, m.name);
        if (cached) return cached as any;
        const out: any = await callModelWithRetry(m, prompt, opts);
        await maybeSetDocsCache(kind, prompt, m.name, out);
        if (typeof out === 'string') return out;
        return { ...out, provider: m.provider, model: m.name };
      } catch (e:any) { lastErr = e; }
    }
    throw lastErr || new Error('no model succeeded');
  }
  const order = await pickOrder(kind);
  for (const p of order) {
    try {
      try { setContext({ provider: p }); } catch {}
      const model = (p==='openai' ? process.env.OPENAI_MODEL : p==='anthropic' ? process.env.ANTHROPIC_MODEL : p==='gemini' ? process.env.GEMINI_MODEL : undefined);
      if (kind === 'docs') {
        const cached = await maybeGetDocsCache(kind, prompt, model || p);
        if (cached) return cached as any;
      }
      const out = await callWithRetry(p, prompt, model as any, 2, 15000, opts);
      if (kind === 'docs') await maybeSetDocsCache(kind, prompt, model || p, out);
      return out;
    } catch (e:any) { lastErr = e; }
  }
  throw lastErr || new Error('no provider succeeded');
}
async function pickOrder(kind: TaskKind): Promise<Array<'openai'|'anthropic'|'gemini'>> {
  const envPref = (process.env.LLM_ORDER || '').split(',').map(x=>x.trim().toLowerCase()).filter(Boolean) as any[];
  if (envPref.length) return envPref as any;
  try {
    const { llm } = await getSettings();
    const order = llm?.order?.[kind as 'docs'|'reasoning'|'codegen'];
    if (Array.isArray(order) && order.length) return order as any;
  } catch {}
  if (kind === 'codegen') return ['openai','anthropic','gemini'];
  if (kind === 'reasoning') return ['anthropic','openai','gemini'];
  return ['gemini','anthropic','openai'];
}
function call(p: string, prompt: string, model?: string, custom?: any, opts?: RouteOpts){
  if (p==='openai') return openaiChat(prompt, model, { maxOutputTokens: opts?.maxOutputTokens });
  if (p==='anthropic') return claudeChat(prompt, model, opts?.maxOutputTokens);
  if (p==='gemini') return geminiChat(prompt, model);
  if (custom && custom.kind === 'openai-compatible') {
    const baseURL = custom.baseUrl || process.env[`LLM_${p.toUpperCase()}_BASE_URL`];
    const apiKeyEnv = process.env[`LLM_${p.toUpperCase()}_API_KEY`] ? `LLM_${p.toUpperCase()}_API_KEY` : undefined;
    return openaiChat(prompt, model, { baseURL, apiKeyEnv, maxOutputTokens: opts?.maxOutputTokens });
  }
  if (custom && custom.kind === 'http') {
    const endpoint = custom.baseUrl || process.env[`LLM_${p.toUpperCase()}_BASE_URL`];
    if (!endpoint) throw new Error('http provider missing baseUrl');
    const apiKeyEnv = process.env[`LLM_${p.toUpperCase()}_API_KEY`] ? `LLM_${p.toUpperCase()}_API_KEY` : undefined;
    return httpChat(prompt, endpoint, apiKeyEnv, model);
  }
  throw new Error('unknown provider ' + p);
}

async function callWithRetry(p: string, prompt: string, model: string|undefined, retries=2, timeoutMs=15000, opts?: RouteOpts){
  let lastErr: any;
  for (let attempt=0; attempt<=retries; attempt++){
    try {
      const { llm } = await getSettings();
      const custom = llm?.providers ? (llm.providers as any)[p] : undefined;
      try { setContext({ retryCount: attempt }); } catch {}
      if (attempt > 0) { try { metrics.retriesTotal.inc({ provider: p }); } catch {} }
      const res = await withTimeout(call(p, prompt, model, custom, opts), timeoutMs);
      return res;
    } catch (e:any) {
      lastErr = e;
      await delay((attempt+1)*250);
    }
  }
  throw lastErr;
}

async function callModelWithRetry(m: { kind: string; base_url?: string; provider: string; name: string }, prompt: string, opts?: RouteOpts, retries=2, timeoutMs=15000){
  let lastErr: any;
  for (let attempt=0; attempt<=retries; attempt++){
    try {
      try { setContext({ provider: m.provider }); } catch {}
      try { setContext({ retryCount: attempt }); } catch {}
      if (attempt > 0) { try { metrics.retriesTotal.inc({ provider: m.provider }); } catch {} }
      const res = await withTimeout(callModel(m, prompt, opts), timeoutMs);
      return res;
    } catch (e:any) {
      lastErr = e;
      await delay((attempt+1)*250);
    }
  }
  throw lastErr;
}

function callModel(m: { kind: string; base_url?: string; provider: string; name: string }, prompt: string, opts?: RouteOpts){
  const kind = (m.kind || '').toLowerCase();
  if (kind === 'openai') return openaiChat(prompt, m.name, { maxOutputTokens: opts?.maxOutputTokens });
  if (kind === 'anthropic') return claudeChat(prompt, m.name, opts?.maxOutputTokens);
  if (kind === 'gemini') return geminiChat(prompt, m.name);
  if (kind === 'openai-compatible') {
    const apiKeyEnv = `LLM_${m.provider.toUpperCase()}_API_KEY`;
    return openaiChat(prompt, m.name, { baseURL: m.base_url, apiKeyEnv, maxOutputTokens: opts?.maxOutputTokens });
  }
  if (kind === 'http') {
    const apiKeyEnv = `LLM_${m.provider.toUpperCase()}_API_KEY`;
    if (!m.base_url) throw new Error('http model missing base_url');
    return httpChat(prompt, m.base_url, apiKeyEnv, m.name);
  }
  throw new Error('unknown model kind ' + kind);
}

function withTimeout<T>(p: Promise<T>, ms: number): Promise<T> {
  return new Promise((resolve, reject) => {
    const t = setTimeout(() => reject(new Error('llm timeout')), ms);
    p.then(v => { clearTimeout(t); resolve(v); }, err => { clearTimeout(t); reject(err); });
  });
}

function delay(ms:number){ return new Promise(r=>setTimeout(r, ms)); }

// ------- Docs cache helpers -------
function cacheKey(kind: TaskKind, prompt: string, model: string) {
  const h = crypto.createHash('sha256').update(prompt).digest('hex').slice(0, 24);
  return `${kind}:${model}:${h}`;
}
async function maybeGetDocsCache(kind: TaskKind, prompt: string, model: string | undefined) {
  if (kind !== 'docs') return undefined;
  const ttlMs = Math.max(0, Number(process.env.DOCS_CACHE_TTL_MS || 10 * 60 * 1000));
  if (ttlMs === 0) return undefined;
  const key = cacheKey(kind, prompt, model || 'default');
  const v = await getCacheJSON('llm', key);
  return v || undefined;
}
async function maybeSetDocsCache(kind: TaskKind, prompt: string, model: string | undefined, value: any) {
  if (kind !== 'docs') return;
  const ttlMs = Math.max(0, Number(process.env.DOCS_CACHE_TTL_MS || 10 * 60 * 1000));
  if (ttlMs === 0) return;
  const key = cacheKey(kind, prompt, model || 'default');
  await setCacheJSON('llm', key, value, ttlMs).catch(()=>{});
}

import { query } from "../lib/db";
export type DbOp = 'insert'|'update'|'delete';
export async function isAllowed(table: string, op: DbOp){
  const r = await query<any>(`select allowed_ops, constraints from nofx.db_write_rule where tenant_id='local' and table_name=$1`, [table]);
  if (!r.rows[0]) return { ok:false, reason:'no rule' };
  const ops: string[] = r.rows[0].allowed_ops || [];
  if (!ops.includes(op)) return { ok:false, reason:'op not allowed' };
  return { ok:true, constraints: r.rows[0].constraints || {} };
}

import { describe, it, expect, beforeAll } from 'vitest';
import { enqueue, STEP_READY_TOPIC, listDlq, STEP_DLQ_TOPIC, rehydrateDlq, subscribe } from './lib/queue';
let store: typeof import('./lib/store').store;
import crypto from 'node:crypto';

describe('Workstream 01  Reliability', () => {
  beforeAll(async () => {
    process.env.QUEUE_DRIVER = 'memory';
    process.env.DATA_DRIVER = 'fs';
    store = (await import('./lib/store')).store;
    // Minimal subscriber to emulate worker inbox semantics for tests
    const STEP_TIMEOUT_MS = 2000;
    function hashInputs(val: any) {
      return crypto.createHash('sha256').update(JSON.stringify(val || {})).digest('hex').slice(0, 12);
    }
    subscribe(STEP_READY_TOPIC, async ({ runId, stepId, idempotencyKey, __attempt }: any) => {
      let key = String(idempotencyKey || '');
      if (!key) {
        const st = await store.getStep(stepId);
        if (st) key = `${runId}:${(st as any).name}:${hashInputs((st as any).inputs)}`;
      }
      if (key) {
        const isNew = await store.inboxMarkIfNew(key).catch(()=> true);
        if (!isNew) return;
      }
      const st = await store.getStep(stepId);
      const tool = (st as any)?.tool;
      if (tool === 'test:fail') {
        throw new Error('fail');
      }
      // echo success
      await store.updateStep(stepId, { status: 'succeeded', ended_at: new Date().toISOString() });
      await store.recordEvent(runId, 'step.started', {}, stepId);
      await new Promise(r => setTimeout(r, 10));
    });
  });

  it('idempotent enqueue/execute: duplicate deliveries ignored', async () => {
    const run = await store.createRun({ goal: 'idempotency', steps: [] });
    const runId = (run as any).id || String(run);
    const step = await store.createStep(runId, 'echo', 'test:echo', { foo: 'bar' });
    const stepId = (step as any).id || String(step);

    // Enqueue the same delivery twice
    await enqueue(STEP_READY_TOPIC, { runId, stepId, __attempt: 1 });
    await enqueue(STEP_READY_TOPIC, { runId, stepId, __attempt: 1 });

    // Wait for processing
    await new Promise(r => setTimeout(r, 200));

    const ev = await store.listEvents(runId);
    const started = ev.filter((e: any) => e.step_id === stepId && e.type === 'step.started');
    expect(started.length).toBe(1);
  });

  it('moves to DLQ after max retries', async () => {
    const run = await store.createRun({ goal: 'dlq', steps: [] });
    const runId = (run as any).id || String(run);
    const step = await store.createStep(runId, 'boom', 'test:fail', { x: 1 });
    const stepId = (step as any).id || String(step);

    // Force immediate DLQ by simulating next-failure beyond backoff schedule
    await enqueue(STEP_READY_TOPIC, { runId, stepId, __attempt: 4 });
    await new Promise(r => setTimeout(r, 100));

    const items = await listDlq(STEP_DLQ_TOPIC);
    const found = (items as any[]).some((p: any) => p && p.stepId === stepId || p?.step_id === stepId);
    expect(items.length).toBeGreaterThan(0);
    expect(found).toBeTruthy();

    // Rehydrate back to ready queue
    const n = await rehydrateDlq(STEP_DLQ_TOPIC, 10);
    expect(n).toBeGreaterThan(0);
  });
});

import { routeLLM } from "../models/router";
import { log } from "../lib/logger";
export type CodegenResult = { content: string; provider?: string; model?: string; usage?: any };

export async function codegenReadme(inputs: { topic?: string; bullets?: string[]; maxOutputTokens?: number } = {}): Promise<CodegenResult> {
  const topic = inputs.topic || "NOFX";
  const bullets = inputs.bullets || ["Control plane", "Verification", "Workers"];
  const hasKeys = !!(process.env.OPENAI_API_KEY || process.env.ANTHROPIC_API_KEY || process.env.GEMINI_API_KEY);
  if (!hasKeys) {
    log.warn("No provider keys set; returning stub content");
    return { content: `# ${topic}\n\n- ${bullets.join("\n- ")}\n\n_Generated locally without LLM._\n`, provider: 'stub', model: 'stub' };
  }
  const prompt = `Write a concise README section titled "${topic}" with bullet points: ${bullets.join(", ")}. Keep it tight.`;
  const res: any = await routeLLM('docs', prompt, { maxOutputTokens: Math.max(1, Number(inputs.maxOutputTokens ?? 800)) });
  if (typeof res === 'string') return { content: res };
  return { content: res.text, provider: res.provider, model: res.model, usage: res.usage };
}

import { StepHandler } from "./types";
import { store } from "../../lib/store";
import { recordEvent } from "../../lib/events";
import { saveArtifact } from "../../lib/artifacts";
import { codegenReadme } from "../../tools/codegen";
import { getSettings } from "../../lib/settings";
import { getModelByName } from "../../lib/models";

const handler: StepHandler = {
  match: (tool) => tool === 'codegen',
  async run({ runId, step }) {
    const stepId = step.id;
    await store.updateStep(stepId, { status: 'running', started_at: new Date().toISOString() });
    await recordEvent(runId, "step.started", { name: step.name, tool: step.tool }, stepId);

    const inputs = step.inputs || {} as any;
    const filename = typeof inputs.filename === 'string' && inputs.filename.trim().length > 0 ? String(inputs.filename).trim() : 'README.md';
    const result = await codegenReadme(inputs || {});
    let costUSD: number | undefined;
    if (result.usage) {
      const { llm } = await getSettings();
      const pricing = llm?.pricing || {};
      const p = (result.provider || '').toLowerCase();
      // Prefer model-specific pricing if defined
      let inP = 0, outP = 0;
      if (result.model) {
        const mr = await getModelByName(result.model);
        if (mr) {
          inP = Number(mr.input_per_1m) || 0;
          outP = Number(mr.output_per_1m) || 0;
        }
      }
      if (!inP && !outP) {
        const price = (pricing as any)[p] || {};
        inP = Number(price.inputPer1M) || 0;
        outP = Number(price.outputPer1M) || 0;
      }
      const inputTokens = result.usage.inputTokens || 0;
      const outputTokens = result.usage.outputTokens || 0;
      costUSD = (inputTokens/1000000)*inP + (outputTokens/1000000)*outP;
      await recordEvent(runId, 'llm.usage', { provider: result.provider, model: result.model, usage: result.usage, costUSD }, stepId);
    }
    if (result.usage) {
      await recordEvent(runId, 'llm.usage', { provider: result.provider, model: result.model, usage: result.usage }, stepId);
    }
    const artifactName = filename;
    const pth = await saveArtifact(runId, stepId, artifactName, result.content, 'text/markdown');
    await store.updateStep(stepId, { status: 'succeeded', ended_at: new Date().toISOString(), outputs: { artifact: pth, provider: result.provider, model: result.model, usage: result.usage } });
    await recordEvent(runId, "step.finished", { artifact: pth, provider: result.provider, model: result.model, costUSD }, stepId);
  }
};
export default handler;

import { StepHandler } from "./types";
import { query } from "../../lib/db";
import { recordEvent } from "../../lib/events";
import { isAllowed, DbOp } from "../../policy/dbWritePolicy";
import { getSettings } from "../../lib/settings";
import { enqueue, STEP_READY_TOPIC } from "../../lib/queue";

type Inputs = {
  table: string;
  op: DbOp;
  values?: Record<string, any>;
  where?: string;          // SQL where clause with $1,$2 params
  whereParams?: any[];     // parameter values for where
};

function isSafeIdent(v: string){
  return /^[a-zA-Z0-9_.]+$/.test(v);
}
function isSafeColumn(v: string){
  return /^[a-zA-Z0-9_]+$/.test(v);
}

const handler: StepHandler = {
  match: (tool) => tool === 'db_write',
  async run({ runId, step }) {
    const stepId = step.id;
    await query(`update nofx.step set status='running', started_at=now() where id=$1`, [stepId])
      .catch(async ()=>{ await query(`update nofx.step set status='running' where id=$1`, [stepId]); });
    await recordEvent(runId, 'step.started', { name: step.name, tool: step.tool }, stepId);

    const inputs: Inputs = step.inputs || {};
    if (!inputs.table || !inputs.op) throw new Error('db_write requires table and op');
    if (!isSafeIdent(inputs.table)) throw new Error('unsafe table name');

    // Check approval policy for db writes
    const { approvals } = await getSettings();
    let needsApproval = false;
    if (approvals.dbWrites === 'all') needsApproval = true;
    if (approvals.dbWrites === 'dangerous' && (inputs.op === 'update' || inputs.op === 'delete')) needsApproval = true;

    if (needsApproval) {
      const g = await query<any>(`select * from nofx.gate where run_id=$1 and step_id=$2 and gate_type='manual:db' order by created_at desc limit 1`, [runId, stepId]);
      if (!g.rows[0]) {
        await query(`insert into nofx.gate (run_id, step_id, gate_type, status) values ($1,$2,'manual:db','pending')`, [runId, stepId]);
        await recordEvent(runId, 'gate.created', { stepId, tool: 'manual:db' }, stepId);
        await enqueue(STEP_READY_TOPIC, { runId, stepId }, { delay: 5000 });
        await recordEvent(runId, 'gate.waiting', { stepId, delayMs: 5000 }, stepId);
        return;
      }
      const gate = g.rows[0];
      if (gate.status === 'pending') {
        await enqueue(STEP_READY_TOPIC, { runId, stepId }, { delay: 5000 });
        await recordEvent(runId, 'gate.waiting', { stepId, delayMs: 5000 }, stepId);
        return;
      }
      if (gate.status === 'failed') {
        await query(`update nofx.step set status='failed', ended_at=now(), error=$2 where id=$1`, [stepId, 'db write not approved'])
          .catch(async ()=>{ await query(`update nofx.step set status='failed', completed_at=now(), error=$2 where id=$1`, [stepId, 'db write not approved']); });
        await recordEvent(runId, 'step.failed', { stepId, tool: step.tool, manual: true, gateId: gate.id }, stepId);
        throw new Error('db write not approved');
      }
      // passed or waived -> continue
    }

    const allowed = await isAllowed(inputs.table, inputs.op);
    if (!allowed.ok) {
      await query(`update nofx.step set status='failed', ended_at=now(), error=$2 where id=$1`, [stepId, `policy: ${allowed.reason}`])
        .catch(async ()=>{ await query(`update nofx.step set status='failed', completed_at=now(), error=$2 where id=$1`, [stepId, `policy: ${allowed.reason}`]); });
      await recordEvent(runId, 'db.write.denied', { table: inputs.table, op: inputs.op, reason: allowed.reason }, stepId);
      throw new Error('db_write not allowed');
    }

    let sql = '';
    const params: any[] = [];
    let result: any = {};
    if (inputs.op === 'insert') {
      const vals = inputs.values || {};
      const cols = Object.keys(vals);
      if (cols.length === 0) throw new Error('insert requires values');
      for (const c of cols) if (!isSafeColumn(c)) throw new Error('unsafe column');
      const placeholders = cols.map((_, i) => `$${i + 1}`);
      params.push(...cols.map(c => vals[c]));
      sql = `insert into ${inputs.table} (${cols.join(',')}) values (${placeholders.join(',')}) returning *`;
    } else if (inputs.op === 'update') {
      const vals = inputs.values || {};
      const cols = Object.keys(vals);
      if (cols.length === 0) throw new Error('update requires values');
      if (!inputs.where) throw new Error('update requires where');
      for (const c of cols) if (!isSafeColumn(c)) throw new Error('unsafe column');
      const sets = cols.map((c, i) => `${c} = $${i + 1}`);
      params.push(...cols.map(c => vals[c]));
      const remappedWhere = remapPlaceholders(inputs.where!, params.length);
      params.push(...(inputs.whereParams || []));
      sql = `update ${inputs.table} set ${sets.join(', ')} where ${remappedWhere} returning *`;
    } else if (inputs.op === 'delete') {
      if (!inputs.where) throw new Error('delete requires where');
      const remappedWhere = remapPlaceholders(inputs.where!, params.length);
      params.push(...(inputs.whereParams || []));
      sql = `delete from ${inputs.table} where ${remappedWhere} returning *`;
    } else {
      throw new Error('unknown op');
    }

    const q = await query<any>(sql, params);
    result = { rowCount: (q as any).rows?.length ?? 0, rows: (q as any).rows ?? [] };

    await query(`update nofx.step set status='succeeded', outputs=$2, ended_at=now() where id=$1`, [
      stepId,
      JSON.stringify({ table: inputs.table, op: inputs.op, result })
    ]).catch(async ()=>{
      await query(`update nofx.step set status='succeeded', outputs=$2, completed_at=now() where id=$1`, [
        stepId,
        JSON.stringify({ table: inputs.table, op: inputs.op, result })
      ]);
    });
    await recordEvent(runId, 'db.write.succeeded', { table: inputs.table, op: inputs.op, rowCount: result.rowCount }, stepId);
  }
};

export default handler;

// Remap $1..$N in a WHERE clause to start at current parameter offset.
function remapPlaceholders(where: string, currentParams: number): string {
  return where.replace(/\$(\d+)/g, (_m, g1) => {
    const n = parseInt(g1, 10);
    if (!Number.isFinite(n) || n <= 0) return _m;
    return `$${currentParams + n}`;
  });
}

import { StepHandler } from "./types";
import { store } from "../../lib/store";
import { recordEvent } from "../../lib/events";
import { log } from "../../lib/logger";
import { getSettings } from "../../lib/settings";
import { saveArtifact } from "../../lib/artifacts";
import { spawnSync } from "node:child_process";
import fs from "node:fs";
import path from "node:path";
import { query } from "../../lib/db";
import { buildMinimalEnv } from "../../lib/secrets";

function contentTypeFor(name: string) {
  const ext = path.extname(name).toLowerCase();
  if (ext === ".json") return "application/json";
  if (ext === ".txt" || ext === ".log") return "text/plain";
  return "application/octet-stream";
}

const handler: StepHandler = {
  match: (tool) => tool.startsWith("gate:"),
  async run({ runId, step }) {
    const stepId = step.id;
    const gateName = step.tool.replace(/^gate:/, "");

    await store.updateStep(stepId, { status: 'running', started_at: new Date().toISOString() });
    await recordEvent(runId, "step.started", { name: step.name, tool: step.tool }, stepId);

    const scriptPath = path.resolve(process.cwd(), "scripts", "runGate.js");
    const { gates } = await getSettings();

    // Skip if gate disabled by settings
    if ((gateName === 'typecheck' && !gates.typecheck) ||
        (gateName === 'lint' && !gates.lint) ||
        (gateName === 'unit' && !gates.unit)) {
      await query(`update nofx.step set status='succeeded', outputs=$2, ended_at=now() where id=$1`, [
        stepId,
        JSON.stringify({ gate: gateName, skipped: true })
      ]).catch(async () => {
        await query(`update nofx.step set status='succeeded', outputs=$2, completed_at=now() where id=$1`, [
          stepId,
          JSON.stringify({ gate: gateName, skipped: true })
        ]);
      });
      await recordEvent(runId, "step.finished", { gate: gateName, skipped: true }, stepId);
      return;
    }

    const policy = (step.inputs && (step.inputs as any)._policy) || {};
    const envAllowed: string[] | undefined = policy.env_allowed;
    const baseEnv = buildMinimalEnv(envAllowed);
    const proc = spawnSync(process.execPath, [scriptPath, gateName], {
      cwd: process.cwd(),
      encoding: "utf8",
      stdio: ["ignore", "pipe", "pipe"],
      env: { ...baseEnv, COVERAGE_THRESHOLD: String(gates.coverageThreshold ?? 0.9) }
    });

    let summary: any = { gate: gateName, passed: proc.status === 0 };
    try {
      const line = (proc.stdout || "").trim().split(/\n/).filter(Boolean).pop();
      if (line) {
        const parsed = JSON.parse(line);
        if (parsed && parsed.summary) summary = parsed.summary;
      }
    } catch (e) {
      // keep default summary
    }

    // Collect and upload evidence artifacts produced by the gate runner
    const localDir = path.resolve(process.cwd(), "gate-artifacts");
    const uploadedPaths: string[] = [];
    if (fs.existsSync(localDir)) {
      const files = fs.readdirSync(localDir);
      for (const f of files) {
        const full = path.join(localDir, f);
        if (!fs.statSync(full).isFile()) continue;
        const storagePath = await saveArtifact(runId, stepId, `gate-artifacts/${f}`, fs.readFileSync(full, 'utf8'), contentTypeFor(f));
        uploadedPaths.push(storagePath);
      }
    }

    // Always upload a JSON summary as gate-summary.json
    const summaryName = "gate-summary.json";
    const summaryPath = await saveArtifact(runId, stepId, summaryName, JSON.stringify(summary, null, 2), 'application/json');
    uploadedPaths.push(summaryPath);

    if (summary.passed) {
      await store.updateStep(stepId, { status: 'succeeded', ended_at: new Date().toISOString(), outputs: { gate: gateName, summary, artifacts: uploadedPaths } });
      await recordEvent(runId, "step.finished", { gate: gateName, summary }, stepId);
    } else {
      await store.updateStep(stepId, { status: 'failed', ended_at: new Date().toISOString(), outputs: { gate: gateName, summary, artifacts: uploadedPaths } });
      await recordEvent(runId, "step.failed", { gate: gateName, summary, stderr: proc.stderr }, stepId);
      throw new Error(`gate ${gateName} failed`);
    }
  }
};

export default handler;

import { StepHandler } from "./types";
import { query } from "../../lib/db";
import { recordEvent } from "../../lib/events";
import { supabase, ARTIFACT_BUCKET } from "../../lib/supabase";
import { log } from "../../lib/logger";
import { spawnSync } from "node:child_process";
import fs from "node:fs";
import path from "node:path";
import { store } from "../../lib/store";
import { enqueue, STEP_READY_TOPIC } from "../../lib/queue";
import { buildMinimalEnv, getSecret } from "../../lib/secrets";

type CommitItem = {
  path: string;               // repo-relative file path
  fromArtifact?: string;      // Supabase storage path of artifact (direct)
  fromStep?: string;          // resolve artifact by prior step name
  artifactName?: string;      // filename within that step (e.g., README.md)
  content?: string;           // inline content
  mode?: 'overwrite';
};
type Inputs = {
  branch?: string;
  base?: string;              // base branch (default: main)
  title?: string;
  body?: string;
  draft?: boolean;
  commits: CommitItem[];
};

function sh(cmd: string, args: string[], cwd: string, env?: NodeJS.ProcessEnv){
  const p = spawnSync(cmd, args, { cwd, stdio: 'pipe', encoding: 'utf8', env: env || process.env });
  if (p.status !== 0) throw new Error(`cmd failed: ${cmd} ${args.join(' ')}\n${p.stderr}`);
  return p.stdout.trim();
}
function repoRoot(){ return process.cwd(); }
function ensureDir(filePath:string){ fs.mkdirSync(path.dirname(filePath), { recursive: true }); }

async function getArtifactBuffer(pth: string): Promise<Buffer> {
  const { data, error } = await supabase.storage.from(ARTIFACT_BUCKET).download(pth);
  if (error || !data) throw new Error(`artifact not found: ${pth}`);
  const arr = await data.arrayBuffer();
  return Buffer.from(arr);
}

function parseOrigin(url: string){
  // supports git@github.com:owner/repo.git or https://github.com/owner/repo.git
  let m = url.match(/github.com[:/](.+?)\/(.+?)(?:\.git)?$/);
  if (!m) throw new Error('unsupported origin url: ' + url);
  return { owner: m[1], repo: m[2] };
}

const handler: StepHandler = {
  match: (tool) => tool === 'git_pr',
  async run({ runId, step }) {
    const stepId = step.id;
    await query(`update nofx.step set status='running', started_at=now() where id=$1`, [stepId])
      .catch(async ()=>{ await query(`update nofx.step set status='running' where id=$1`, [stepId]); });
    await recordEvent(runId, 'step.started', { name: step.name, tool: step.tool }, stepId);

    // Default manual approval for git_pr
    try {
      const g = await store.getLatestGate(runId, stepId);
      if (!g) {
        await store.createOrGetGate(runId, stepId, 'manual:git_pr');
        await recordEvent(runId, 'gate.created', { stepId, tool: 'manual:git_pr' }, stepId);
        await enqueue(STEP_READY_TOPIC, { runId, stepId }, { delay: 5000 });
        await recordEvent(runId, 'gate.waiting', { stepId, delayMs: 5000 }, stepId);
        return;
      }
      if ((g as any).status === 'pending') {
        await enqueue(STEP_READY_TOPIC, { runId, stepId }, { delay: 5000 });
        await recordEvent(runId, 'gate.waiting', { stepId, delayMs: 5000 }, stepId);
        return;
      }
      if ((g as any).status === 'failed') {
        await query(`update nofx.step set status='failed', ended_at=now(), error=$2 where id=$1`, [stepId, 'git_pr not approved'])
          .catch(async ()=>{ await query(`update nofx.step set status='failed', completed_at=now(), error=$2 where id=$1`, [stepId, 'git_pr not approved']); });
        await recordEvent(runId, 'step.failed', { stepId, tool: step.tool, manual: true, gateId: (g as any).id }, stepId);
        throw new Error('git_pr not approved');
      }
    } catch {/* non-fatal: continue if store not available */}

    const inputs: Inputs = step.inputs || {};
    if (!Array.isArray(inputs.commits) || inputs.commits.length === 0) throw new Error('git_pr requires commits');
    const base = inputs.base || process.env.GIT_DEFAULT_BASE || 'main';
    const branch = inputs.branch || `feat/run-${runId.slice(0,8)}`;
    const repo = repoRoot();
    const policy = (step.inputs && (step.inputs as any)._policy) || {};
    const envAllowed: string[] | undefined = policy.env_allowed;
    const env = buildMinimalEnv(envAllowed);

    // prepare files
    for (const c of inputs.commits) {
      const outPath = path.join(repo, c.path);
      ensureDir(outPath);
      if (c.fromArtifact) {
        const buf = await getArtifactBuffer(c.fromArtifact);
        fs.writeFileSync(outPath, buf);
      } else if (c.fromStep && c.artifactName) {
        // resolve artifact by step name and filename
        const stepRow = await query<any>(`select id from nofx.step where run_id=$1 and name=$2 limit 1`, [runId, c.fromStep]);
        const sid = stepRow.rows[0]?.id;
        if (!sid) throw new Error(`step not found: ${c.fromStep}`);
        const art = await query<any>(`select path as uri from nofx.artifact where step_id=$1 and path like $2 limit 1`, [sid, `%/${c.artifactName}`]);
        const pth = (art.rows[0] as any)?.uri as string | undefined;
        if (!pth) throw new Error(`artifact not found: ${c.artifactName} in step ${c.fromStep}`);
        const buf = await getArtifactBuffer(pth);
        fs.writeFileSync(outPath, buf);
      } else if (typeof c.content === 'string') {
        fs.writeFileSync(outPath, c.content, 'utf8');
      } else {
        throw new Error('commit item requires fromArtifact or content');
      }
    }

    // git plumbing
    try { sh('git', ['rev-parse','--is-inside-work-tree'], repo, env); } catch { throw new Error('not a git repo'); }
    sh('git', ['fetch','origin', base], repo, env);
    // Create branch from origin/base
    sh('git', ['checkout','-B', branch, `origin/${base}`], repo, env);
    sh('git', ['add', '--all'], repo, env);
    const commitMsg = inputs.title || `Update by NOFX run ${runId}`;
    // set identity if needed
    try { sh('git', ['config','user.email'], repo, env); } catch { sh('git', ['config','user.email', 'nofx@example.com'], repo, env); }
    try { sh('git', ['config','user.name'], repo, env); } catch { sh('git', ['config','user.name', 'NOFX Bot'], repo, env); }
    sh('git', ['commit','-m', commitMsg], repo, env);
    sh('git', ['push','-u','origin', branch], repo, env);

    // Open PR via GitHub API
    let prUrl: string | undefined;
    try {
      const origin = sh('git', ['config','--get','remote.origin.url'], repo, env);
      const { owner, repo: name } = parseOrigin(origin);
      const token = getSecret('GITHUB_TOKEN', { runId, scope: policy?.secrets_scope || 'github', allowEnv: true, envAllowed })
        || getSecret('GH_TOKEN', { runId, scope: policy?.secrets_scope || 'github', allowEnv: true, envAllowed });
      if (!token) throw new Error('GITHUB_TOKEN not set');
      const rsp = await fetch(`https://api.github.com/repos/${owner}/${name}/pulls`, {
        method: 'POST',
        headers: { 'Authorization': `token ${token}`, 'Content-Type': 'application/json', 'Accept': 'application/vnd.github+json' },
        body: JSON.stringify({
          title: inputs.title || commitMsg,
          head: branch,
          base,
          body: inputs.body || `Automated PR from NOFX run ${runId}`,
          draft: !!inputs.draft
        })
      } as any);
      if ((rsp as any).ok) {
        const data: any = await (rsp as any).json();
        prUrl = data.html_url;
      } else {
        const t = await (rsp as any).text();
        throw new Error(`create PR failed: ${t}`);
      }
    } catch (e:any) {
      log.warn({ err: e?.message }, 'PR creation failed; commit pushed');
    }

    const outputs = { branch, base, prUrl, files: inputs.commits.map(c=>c.path) };
    await query(`update nofx.step set status='succeeded', outputs=$2, ended_at=now() where id=$1`, [
      stepId, JSON.stringify(outputs)
    ]).catch(async ()=>{
      await query(`update nofx.step set status='succeeded', outputs=$2, completed_at=now() where id=$1`, [
        stepId, JSON.stringify(outputs)
      ]);
    });
    await recordEvent(runId, 'step.finished', outputs, stepId);
  }
};

export default handler;

import fs from 'node:fs';
import path from 'node:path';
import type { StepHandler } from './types';

export function loadHandlers(): StepHandler[] {
  const dir = __dirname;
  const handlers: StepHandler[] = [];
  const all = fs.readdirSync(dir);
  const files = (process.env.NODE_ENV === 'test')
    ? all.filter(f => /^(test[_\.].*|.*test.*)\.(ts|js)$/.test(f))
    : all;
  for (const file of files) {
    if (!/\.(ts|js)$/.test(file)) continue;
    if (['loader.ts','types.ts'].includes(file)) continue;
    const mod = require(path.join(dir, file));
    const h: StepHandler | undefined = mod.default || mod.handler;
    if (h && typeof h.run === 'function' && typeof h.match === 'function') handlers.push(h);
  }
  return handlers;
}

import { StepHandler } from "./types";
import { store } from "../../lib/store";
import { recordEvent } from "../../lib/events";
import { enqueue, STEP_READY_TOPIC } from "../../lib/queue";

const CHECK_DELAY_MS = 5000;

const handler: StepHandler = {
  match: (tool) => tool.startsWith("manual:"),
  async run({ runId, step }) {
    const stepId = step.id;
    // ensure step is marked running
    await store.updateStep(stepId, { status: 'running', started_at: new Date().toISOString() });

    // does a gate exist for this step?
    const g = await store.getLatestGate(runId, stepId);
    if (!g) {
      await store.createOrGetGate(runId, stepId, step.tool);
      await recordEvent(runId, 'gate.created', { stepId, tool: step.tool }, stepId);
      // re-enqueue to check later
      await enqueue(STEP_READY_TOPIC, { runId, stepId }, { delay: CHECK_DELAY_MS });
      await recordEvent(runId, 'gate.waiting', { stepId, delayMs: CHECK_DELAY_MS }, stepId);
      return;
    }

    const gate = g as any;
    if (gate.status === 'pending') {
      await enqueue(STEP_READY_TOPIC, { runId, stepId }, { delay: CHECK_DELAY_MS });
      await recordEvent(runId, 'gate.waiting', { stepId, delayMs: CHECK_DELAY_MS }, stepId);
      return;
    }

    if (gate.status === 'passed' || gate.status === 'waived') {
      await store.updateStep(stepId, { status: 'succeeded', ended_at: new Date().toISOString(), outputs: { manual: true, gateId: (gate as any).id, status: gate.status } });
      await recordEvent(runId, 'step.finished', { stepId, tool: step.tool, manual: true, gateId: gate.id }, stepId);
      return;
    }

    if (gate.status === 'failed') {
      await store.updateStep(stepId, { status: 'failed', ended_at: new Date().toISOString(), outputs: { manual: true, gateId: (gate as any).id, status: gate.status } });
      await recordEvent(runId, 'step.failed', { stepId, tool: step.tool, manual: true, gateId: gate.id }, stepId);
      throw new Error('manual gate failed');
    }
  }
};

export default handler;

